import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const AdicionarOferta = () => {
    const navigate = useNavigate();
    const { token } = useContext(AuthContext);

    // Estados para o fluxo
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [selectedVariation, setSelectedVariation] = useState('');
    const [preco, setPreco] = useState('');
    const [quantidade, setQuantidade] = useState('');
    const [loading, setLoading] = useState(false);
    const [notification, setNotification] = useState({ message: '', type: '' });
    
    // ESTADOS PARA MÚLTIPLAS OFERTAS E NOVAS VARIAÇÕES
    const [offers, setOffers] = useState({});
    const [showNewVariationForm, setShowNewVariationForm] = useState(false);
    const [newVariationName, setNewVariationName] = useState('');
    const [newVariationValue, setNewVariationValue] = useState('');

    // Efeito para buscar produtos enquanto o usuário digita
    useEffect(() => {
        if (searchTerm.length < 3) {
            setSearchResults([]);
            return;
        }

        const delayDebounceFn = setTimeout(() => {
            setLoading(true);
            axios.get(`/api/produtos/?search=${searchTerm}`, { headers: { Authorization: `Bearer ${token}` } })
                .then(response => {
                    setSearchResults(response.data);
                })
                .catch(err => console.error("Erro na busca:", err))
                .finally(() => setLoading(false));
        }, 500); // Espera 500ms após o usuário parar de digitar

        return () => clearTimeout(delayDebounceFn);
    }, [searchTerm, token]);

    // Função para recarregar o produto selecionado (útil após criar nova variação)
    const reloadSelectedProduct = async (productId) => {
        try {
            const response = await axios.get(`/api/produtos/${productId}/`, { headers: { Authorization: `Bearer ${token}` } });
            setSelectedProduct(response.data);
            
            // Reinicia a lista de ofertas com as novas variações
            const initialOffers = response.data.variacoes.reduce((acc, v) => {
                acc[v.id] = { preco: '', quantidade: '' };
                return acc;
            }, {});
            setOffers(initialOffers);

        } catch (error) {
            console.error("Erro ao recarregar o produto", error);
            showNotification("Não foi possível atualizar a lista de variações.", "error");
        }
    };


    const showNotification = (message, type) => {
        setNotification({ message, type });
        setTimeout(() => setNotification({ message: '', type: '' }), 5000);
    };

    const handleSelectProduct = (product) => {
        setSelectedProduct(product);
        setSearchTerm('');
        setSearchResults([]);
    };
    
    const handleResetSelection = () => {
        setSelectedProduct(null);
        setOffers({});
    };

    const handleSaveOffer = async (e) => {
        e.preventDefault();
        if (!selectedVariation || !preco || !quantidade) {
            showNotification('Preencha todos os campos da oferta.', 'error');
            return;
        }

        try {
            await axios.post('/api/ofertas/', {
                variacao: selectedVariation,
                preco: preco,
                quantidade_disponivel: quantidade,
            }, { headers: { Authorization: `Bearer ${token}` } });
            
            showNotification('Nova oferta cadastrada com sucesso!', 'success');
            handleResetSelection();

        } catch (err) {
            console.error("Erro ao salvar oferta:", err.response.data);
            const errorMessage = err.response?.data?.non_field_errors?.[0] || 'Falha ao cadastrar oferta. Você já não possui uma oferta para esta variação?';
            showNotification(errorMessage, 'error');
        }
    };

    return (
        <div>
            {notification.message && <div className={`notification ${notification.type}`}>{notification.message}</div>}
            
            <h1 className="apresentacao__conteudo__titulo">Adicionar Nova Oferta</h1>

            {!selectedProduct ? (
                // --- PASSO 1: BUSCA DO PRODUTO ---
                <div className="form-container" style={{maxWidth: '700px'}}>
                    <div className="form-group">
                        <label>Primeiro, busque por um produto já existente no catálogo:</label>
                        <input
                            type="text"
                            placeholder="Digite o nome do produto (ex: Leite Ninho)"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                    </div>

                    {loading && <p>Buscando...</p>}

                    {searchResults.length > 0 && (
                        <ul className="search-results-list">
                            {searchResults.map(prod => (
                                <li key={prod.id} onClick={() => handleSelectProduct(prod)}>
                                    <strong>{prod.nome}</strong>
                                    <p>{prod.descricao}</p>
                                </li>
                            ))}
                        </ul>
                    )}
                    
                    <div style={{textAlign: 'center', marginTop: '30px'}}>
                        <p>Não encontrou o produto que procurava?</p>
                        <button onClick={() => navigate('/cadastrar-produto')} className="btn btn-secondary">Cadastrar um Produto Completamente Novo</button>
                    </div>
                </div>
            ) : (
                // --- PASSO 2: CADASTRO DA OFERTA ---
                <div className="form-container" style={{maxWidth: '700px'}}>
                    <div className="selected-product-info">
                        <h3>Produto Selecionado: {selectedProduct.nome}</h3>
                        <p>{selectedProduct.descricao}</p>
                        <button onClick={handleResetSelection} className="btn-link">Buscar outro produto</button>
                    </div>
                    
                    <form onSubmit={handleSaveOffer}>
                        <div className="form-group">
                            <label>Selecione a Variação do produto:</label>
                            <select value={selectedVariation} onChange={(e) => setSelectedVariation(e.target.value)} required>
                                <option value="">-- Selecione uma variação --</option>
                                {/* O ideal é que as variações venham junto com a busca do produto.
                                    Por enquanto, vamos simular ou deixar para o usuário preencher */}
                                {selectedProduct.variacoes?.map(v => (
                                   <option key={v.id} value={v.id}>{v.nome}: {v.valor}</option>
                                )) || <option disabled>Este produto não tem variações cadastradas</option>}
                            </select>
                        </div>

                        <div className="form-group">
                            <label>Seu Preço (R$):</label>
                            <input type="number" value={preco} onChange={(e) => setPreco(e.target.value)} step="0.01" required />
                        </div>

                        <div className="form-group">
                            <label>Quantidade em Estoque:</label>
                            <input type="number" value={quantidade} onChange={(e) => setQuantidade(e.target.value)} required />
                        </div>

                        <div className="form-actions">
                            <button type="submit" className="btn btn-primary">Adicionar minha Oferta</button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
};

export default AdicionarOferta;
