import React, { useContext } from 'react';
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Navigate
} from 'react-router-dom';

// Importa o Contexto e os Componentes de Rota
import { AuthProvider, AuthContext } from './context/AuthContext';
import RotaProtegida from './components/RotaProtegida';

// Importa as páginas
import Login from './pages/Login';
import Cadastro from './pages/Cadastro';
import Inicio from './pages/Inicio';

// Componentes de página para demonstração. Substitua pelos seus componentes reais.
const PainelVendedor = () => <h2>Painel do Vendedor</h2>;
const BuscaCliente = () => <h2>Busca de Produtos para Clientes</h2>;
const NaoAutorizado = () => <h2>Acesso Não Autorizado</h2>;

// Este componente especial redireciona o usuário para o painel correto após o login.
const RedirecionamentoPainel = () => {
    const { usuario } = useContext(AuthContext);

    // Mostra uma mensagem de carregamento enquanto o usuário é verificado
    if (!usuario) {
        return <div>Carregando...</div>;
    }

    // Redireciona com base no tipo de usuário
    if (usuario.tipoUsuario === 'vendedor') {
        return <Navigate to="/vendedor/painel" replace />;
    }
    if (usuario.tipoUsuario === 'cliente') {
        return <Navigate to="/cliente/busca" replace />;
    }

    // Se o tipo de usuário for inválido, volta para o login
    return <Navigate to="/login" replace />;
};

import Estrutura from './components/Estrutura';

function App() {
    return (
        <AuthProvider>
            <Router>
                <Estrutura>
                    <Routes>
                        {/* --- Rotas Públicas --- */}
                        <Route path="/" element={<Inicio />} />
                        <Route path="/login" element={<Login />} />
                        <Route path="/cadastro" element={<Cadastro />} />
                        <Route path="/nao-autorizado" element={<NaoAutorizado />} />

                        {/* --- Rotas Protegidas --- */}
                        <Route path="/painel" element={<RotaProtegida><RedirecionamentoPainel /></RotaProtegida>} />

                        {/* Rotas específicas para VENDEDOR */}
                        <Route path="/vendedor/painel" element={<RotaProtegida papeisPermitidos={['vendedor']}><PainelVendedor /></RotaProtegida>} />

                        {/* Rotas específicas para CLIENTE */}
                        <Route path="/cliente/busca" element={<RotaProtegida papeisPermitidos={['cliente']}><BuscaCliente /></RotaProtegida>} />

                    </Routes>
                </Estrutura>
            </Router>
        </AuthProvider>
    );
}

export default App;
