import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const FormularioLogin = () => {
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [erro, setErro] = useState('');
    const [mensagemSucesso, setMensagemSucesso] = useState('');

    const { login } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErro('');
        setMensagemSucesso('');

        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/autenticacao/login`, { email, password: senha });

            login(response.data.token);

            navigate('/painel'); 

        } catch (err) {
            const mensagem = err.response?.data?.message || 'Falha no login. Verifique suas credenciais.';
            setErro(mensagem);

            if (mensagem.includes('Conta inativa')) {
                navigate('/verificar-email', { state: { email: email } });
            }
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Login</h2>
            {erro && <p style={{ color: 'red' }}>{erro}</p>}
            {mensagemSucesso && <p style={{ color: 'green' }}>{mensagemSucesso}</p>}
            <div>
                <label>Email:</label>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Senha:</label>
                <input
                    type="password"
                    value={senha}
                    onChange={(e) => setSenha(e.target.value)}
                    required
                />
            </div>
            <button type="submit">Entrar</button>
        </form>
    );
};

export default FormularioLogin;
