import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const DashboardVendedor = () => {
  const navigate = useNavigate();
  const { token } = useContext(AuthContext);
  const [avaliacoes, setAvaliacoes] = useState([]);
  const [loadingAvaliacoes, setLoadingAvaliacoes] = useState(true);
  const [errorAvaliacoes, setErrorAvaliacoes] = useState('');



  useEffect(() => {
    const fetchAvaliacoes = async () => {
      try {
        const response = await axios.get('/api/usuarios/avaliacoes', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setAvaliacoes(response.data);
      } catch (err) {
        setErrorAvaliacoes('Falha ao buscar suas avaliações.');
        console.error(err);
      }
      setLoadingAvaliacoes(false);
    };

    if (token) {
      fetchAvaliacoes();
    }
  }, [token]);

  // Dados de exemplo para a lista de produtos mais acessados
  const produtosMaisAcessados = [
    { id: 1, nome: 'Produto A', acessos: 150 },
    { id: 2, nome: 'Produto B', acessos: 120 },
    { id: 3, nome: 'Produto C', acessos: 95 },
  ];

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">Painel do Vendedor</h1>
      <p className="text" style={{ textAlign: 'center', marginBottom: '30px' }}>Bem-vindo ao seu painel! Gerencie seus produtos e acompanhe seu desempenho.</p>

      {/* Seção de Gerenciamento de Produtos */}
      <div className="card-grid">
        <div className="card">
          <span className="card-icon">📦</span> {/* Ícone de pacote */}
          <h2 className="card-title">Gerenciamento de Produtos</h2>
          <p className="card-text">Adicione novos produtos ou visualize e edite os existentes.</p>
          <button onClick={() => navigate('/cadastrar-produto')} className="btn btn-primary">Cadastrar Novo Produto</button>
          <button onClick={() => navigate('/meus-produtos')} className="btn btn-secondary" style={{ marginTop: '10px' }}>Ver Meus Produtos</button>
        </div>

        {/* Seção de Análise */}
        <div className="card">
          <span className="card-icon">📈</span> {/* Ícone de gráfico */}
          <h2 className="card-title">Produtos Mais Acessados</h2>
          <p className="card-text">Acompanhe quais produtos estão gerando mais interesse.</p>
          <ul>
            {produtosMaisAcessados.map(produto => (
              <li key={produto.id} className="text" style={{ marginBottom: '5px' }}>
                {produto.nome} - <strong>{produto.acessos} visualizações</strong>
              </li>
            ))}
          </ul>
        </div>

        {/* Seção de Conta */}
        <div className="card">
          <span className="card-icon">⚙️</span> {/* Ícone de engrenagem */}
          <h2 className="card-title">Minha Conta</h2>
          <p className="card-text">Edite suas informações de perfil e dados da loja.</p>
          <button className="btn btn-secondary">Editar Perfil e Dados da Loja</button>
          <p className="text" style={{ fontSize: '0.8rem', marginTop: '10px' }}><small>Obs: A alteração de dados como CNPJ requer aprovação de um administrador.</small></p>
        </div>

        {/* Seção de Avaliações */}
        <div className="card">
          <span className="card-icon">⭐</span> {/* Ícone de estrela */}
          <h2 className="card-title">Minhas Avaliações</h2>
          {loadingAvaliacoes ? (
            <p>Carregando avaliações...</p>
          ) : errorAvaliacoes ? (
            <p>{errorAvaliacoes}</p>
          ) : avaliacoes.length === 0 ? (
            <p>Você ainda não possui avaliações.</p>
          ) : (
            <div className="evaluation-list">
              {avaliacoes.map(avaliacao => (
                <div key={avaliacao.ID_Avaliacao} className="evaluation-card">
                  <p><strong>Nota:</strong> {avaliacao.Nota}/5</p>
                  <p><strong>Comentário:</strong> {avaliacao.Comentario}</p>
                  <p><small>Avaliado em: {new Date(avaliacao.DataAvaliacao).toLocaleDateString()}</small></p>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Seção de Indicação de Vendedor */}
        <div className="card">
          <span className="card-icon">🤝</span> {/* Ícone de aperto de mãos */}
          <h2 className="card-title">Indicar Novo Vendedor</h2>
          <p className="card-text">Conhece alguém que deveria vender aqui? Indique-o!</p>
          <form onSubmit={handleIndicacaoSubmit} className="form-container">
            <div className="form-group">
              <label>Nome do Indicado:</label>
              <input
                type="text"
                name="nomeIndicado"
                value={indicacao.nomeIndicado}
                onChange={handleIndicacaoChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Email do Indicado:</label>
              <input
                type="email"
                name="emailIndicado"
                value={indicacao.emailIndicado}
                onChange={handleIndicacaoChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Telefone do Indicado (opcional):</label>
              <input
                type="text"
                name="telefoneIndicado"
                value={indicacao.telefoneIndicado}
                onChange={handleIndicacaoChange}
              />
            </div>
            <div className="form-group">
              <label>Mensagem (opcional):</label>
              <textarea
                name="mensagem"
                value={indicacao.mensagem}
                onChange={handleIndicacaoChange}
              ></textarea>
            </div>
            <button type="submit" className="btn btn-primary">Enviar Indicação</button>
          </form>
          {indicacaoStatus && <p className="message-info">{indicacaoStatus}</p>}
        </div>
      </div>

      <div style={{ textAlign: 'center', marginTop: '30px' }}>
        <button onClick={() => navigate('/')} className="btn btn-secondary">Voltar ao Início</button>
      </div>
    </div>
  );
};

export default DashboardVendedor;
