import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { jwtDecode } from 'jwt-decode';

const FormularioLogin = () => {
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [erro, setErro] = useState('');
    const [mensagemSucesso, setMensagemSucesso] = useState('');

    const { login } = useContext(AuthContext);
    const navegar = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErro('');
        setMensagemSucesso('');

        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/login/`, { email, senha });

            const decodedToken = jwtDecode(response.data.token);
            const isEmailVerified = decodedToken.email_verificado;

            login(response.data.token, response.data.perfil_completo, email, isEmailVerified);

            if (!response.data.perfil_completo) {
                navegar('/completar-perfil', { state: { idUsuario: decodedToken.user_id, tipoUsuario: decodedToken.tipo_usuario, email: email } });
            } else {
                if (isEmailVerified) {
                    navegar('/painel');
                } else {
                    navegar('/verificar-email', { state: { email: email } });
                }
            } 

        } catch (err) {
            const errorDetail = err.response?.data?.detail;
            const errorMessage = err.response?.data?.message || 'Falha no login. Verifique suas credenciais.';

            if (errorDetail === 'EMAIL_NAO_VERIFICADO') {
                setErro(errorMessage);
                navegar('/verificar-email', { state: { email: email } });
            } else {
                setErro(errorMessage);
            }
        }
    };

    return (
        <form onSubmit={handleSubmit} className="form-container">
            <h2>Login</h2>
            {erro && <p className="message-error">{erro}</p>}
            {mensagemSucesso && <p className="message-success">{mensagemSucesso}</p>}
            <div className="form-group">
                <label>Email:</label>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label>Senha:</label>
                <input
                    type="password"
                    value={senha}
                    onChange={(e) => setSenha(e.target.value)}
                    onKeyPress={(e) => { if (e.key === 'Enter') handleSubmit(e); }}
                    required
                />
            </div>
            <div className="form-actions">
                <button type="submit" className="btn btn-primary">Entrar</button>
            </div>
        </form>
    );
};

export default FormularioLogin;
