import React, { useState } from 'react';
import axios from 'axios';

const RecuperarSenha = () => {
    const [email, setEmail] = useState('');
    const [notification, setNotification] = useState({ message: '', type: '' });

    const showNotification = (message, type) => {
        setNotification({ message, type });
        setTimeout(() => {
            setNotification({ message: '', type: '' });
        }, 3000);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/recuperar-senha/', { email });
            showNotification('Um e-mail de recuperação foi enviado para o seu endereço.', 'success');
        } catch (err) {
            showNotification('Falha ao enviar e-mail de recuperação. Verifique o e-mail digitado.', 'error');
        }
    };

    return (
        <main className="apresentacao">
            <section className="apresentacao__conteudo">
                <h1 className="apresentacao__conteudo__titulo" style={{ textAlign: 'center' }}>
                    Recuperar <strong className="titulodestaque">Senha</strong>
                </h1>
                <form onSubmit={handleSubmit} className="formulario-login">
                    <div className="form-group">
                        <label htmlFor="email">E-mail</label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="btn btn-primary">Enviar</button>
                </form>
                {notification.message && (
                    <div className={`notification ${notification.type}`}>
                        {notification.message}
                    </div>
                )}
            </section>
        </main>
    );
};

export default RecuperarSenha;
