import React, { createContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [usuario, setUsuario] = useState(null);
    const [token, setToken] = useState(localStorage.getItem('token'));
    const [perfilCompleto, setPerfilCompleto] = useState(() => {
        const perfilSalvo = localStorage.getItem('perfilCompleto');
        return perfilSalvo ? JSON.parse(perfilSalvo) : false;
    });

    useEffect(() => {
        if (token) {
            try {
                const tokenDecodificado = jwtDecode(token);
                if (tokenDecodificado.exp * 1000 < Date.now()) {
                    logout();
                } else {
                    setUsuario({ id: tokenDecodificado.id, tipoUsuario: tokenDecodificado.tipo });
                }
            } catch (error) {
                console.error("Token inválido", error);
                logout();
            }
        }
    }, [token]);

    const login = (novoToken, statusPerfil) => {
        localStorage.setItem('token', novoToken);
        localStorage.setItem('perfilCompleto', JSON.stringify(statusPerfil));
        setToken(novoToken);
        setPerfilCompleto(statusPerfil);
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('perfilCompleto');
        setToken(null);
        setUsuario(null);
        setPerfilCompleto(false);
    };

    const atualizarStatusPerfil = (status) => {
        localStorage.setItem('perfilCompleto', JSON.stringify(status));
        setPerfilCompleto(status);
    };

    const valorDoContexto = {
        usuario,
        token,
        perfilCompleto,
        login,
        logout,
        atualizarStatusPerfil,
    };

    return <AuthContext.Provider value={valorDoContexto}>{children}</AuthContext.Provider>;
};
