import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
// import { jwtDecode } from 'jwt-decode';

const FormularioLogin = () => {
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [erro, setErro] = useState('');
    const [mensagemSucesso, setMensagemSucesso] = useState('');

    const { login } = useContext(AuthContext);
    const navegar = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErro('');
        setMensagemSucesso('');

        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/login/`, { email, senha });

            // O backend agora retorna o token e um objeto 'user'
            const { access: token, user: dadosUsuarioOriginal } = response.data;

            // Criar uma cópia profunda para garantir que seja um objeto JavaScript simples
            const dadosUsuario = JSON.parse(JSON.stringify(dadosUsuarioOriginal));

            // Chama a nova função de login do contexto
            login(token, dadosUsuario);

            console.log('Dados do usuário para redirecionamento:', dadosUsuario);

            // Lógica de redirecionamento: sempre navega para /painel após o login bem-sucedido.
            // O componente RedirecionamentoPainel (em App.jsx) cuidará dos redirecionamentos adicionais
            // baseados no status do perfil e verificação de e-mail.
            navegar('/painel');

        } catch (err) {
            const errorDetail = err.response?.data?.detail;
            const errorMessage = err.response?.data?.message || 'Falha no login. Verifique suas credenciais.';

            if (errorDetail === 'EMAIL_NAO_VERIFICADO') {
                setErro(errorMessage);
                navegar('/verificar-email', { state: { email: email } });
            } else {
                setErro(errorMessage);
            }
        }
    };

    return (
        <form onSubmit={handleSubmit} className="form-container">
            <h2>Login</h2>
            {erro && <p className="message-error">{erro}</p>}
            {mensagemSucesso && <p className="message-success">{mensagemSucesso}</p>}
            <div className="form-group">
                <label>Email:</label>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label>Senha:</label>
                <input
                    type="password"
                    value={senha}
                    onChange={(e) => setSenha(e.target.value)}
                    onKeyPress={(e) => { if (e.key === 'Enter') handleSubmit(e); }}
                    required
                />
            </div>
            <div className="form-actions">
                <button type="submit" className="btn btn-primary">Entrar</button>
            </div>
        </form>
    );
};

export default FormularioLogin;
