// Formulário para dados do Vendedor
const FormularioVendedor = ({ idUsuario, aoEnviar }) => {
    const [nomeLoja, setNomeLoja] = useState('');
    const [cnpj, setCnpj] = useState('');
    const [rua, setRua] = useState('');
    const [numero, setNumero] = useState('');
    const [complemento, setComplemento] = useState('');
    const [bairro, setBairro] = useState('');
    const [cidade, setCidade] = useState('');
    const [estado, setEstado] = useState('');
    const [cep, setCep] = useState('');
    const [telefone, setTelefone] = useState('');
    const [fundacao, setFundacao] = useState('');
    const [horarioFuncionamento, setHorarioFuncionamento] = useState('');
    const [nomeResponsavel, setNomeResponsavel] = useState('');
    const [cpfResponsavel, setCpfResponsavel] = useState('');
    const [breveDescricaoLoja, setBreveDescricaoLoja] = useState('');
    const [logotipoLoja, setLogotipoLoja] = useState('');
    const [siteRedesSociais, setsiteRedesSociais] = useState('');
    const [idCategoriaLoja, setIdCategoriaLoja] = useState('');
    const [categorias, setCategorias] = useState([]);
    const [descricaoCategoria, setDescricaoCategoria] = useState('');
    const [erros, setErros] = useState({});

    useEffect(() => {
        const buscarCategorias = async () => {
            try {
                                console.log('Buscando categorias de:', `${process.env.REACT_APP_API_URL}/api/categorias/`);
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/categorias/`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                console.log('Categorias recebidas:', response.data);
                setCategorias(response.data);
            } catch (error) {
                console.error('Erro ao buscar categorias:', error.response ? error.response.data : error.message);
            }
        };
        buscarCategorias();
    }, []);

    useEffect(() => {
        const categoriaSelecionada = categorias.find(cat => cat.ID_CategoriaLoja === parseInt(idCategoriaLoja));
        if (categoriaSelecionada) {
            setDescricaoCategoria(categoriaSelecionada.Descricao);
        } else {
            setDescricaoCategoria('');
        }
    }, [idCategoriaLoja, categorias]);

    const validarCampos = () => {
        let novosErros = {};
        if (!nomeLoja) novosErros.nomeLoja = 'Nome da Loja é obrigatório.';
        if (!cnpj) {
            novosErros.cnpj = 'CNPJ é obrigatório.';
        } else if (cnpj.length !== 14 || !/^[0-9]+$/.test(cnpj)) {
            novosErros.cnpj = 'CNPJ deve conter 14 dígitos numéricos.';
        }
        if (!rua) novosErros.rua = 'Rua é obrigatória.';
        if (!numero) novosErros.numero = 'Número é obrigatório.';
        if (!bairro) novosErros.bairro = 'Bairro é obrigatório.';
        if (!cidade) novosErros.cidade = 'Cidade é obrigatória.';
        if (!estado) novosErros.estado = 'Estado é obrigatório.';
        if (!cep) novosErros.cep = 'CEP é obrigatório.';
        if (!idCategoriaLoja) novosErros.idCategoriaLoja = 'Categoria da Loja é obrigatória.';

        setErros(novosErros);
        return Object.keys(novosErros).length === 0;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (validarCampos()) {
            aoEnviar({
                idUsuario, 
                nomeLoja, 
                cnpj, 
                telefone, 
                fundacao, 
                horarioFuncionamento,
                nomeResponsavel, 
                cpfResponsavel, 
                breveDescricaoLoja, 
                logotipoLoja, 
                siteRedesSociais,
                idCategoriaLoja,
                endereco: {
                    logradouro: rua,
                    numero,
                    complemento,
                    bairro,
                    cidade,
                    estado,
                    cep
                }
            });
        }
    };

    return (
        <form onSubmit={handleSubmit} className="form-container">
            <h3>Complete seu Perfil de Vendedor</h3>
            {erros.nomeLoja && <p className="message-error">{erros.nomeLoja}</p>}
            <div className="form-group">
                <label htmlFor="nomeLoja">Nome da Loja</label>
                <input type="text" id="nomeLoja" value={nomeLoja} onChange={(e) => setNomeLoja(e.target.value)} placeholder="Nome da Loja" required />
            </div>

            {erros.cnpj && <p className="message-error">{erros.cnpj}</p>}
            <div className="form-group">
                <label htmlFor="cnpj">CNPJ</label>
                <input type="text" id="cnpj" value={cnpj} onChange={(e) => setCnpj(e.target.value)} placeholder="CNPJ" />
            </div>

            <h4>Endereço</h4>
            {erros.rua && <p className="message-error">{erros.rua}</p>}
            <div className="form-group">
                <label htmlFor="ruaVendedor">Rua</label>
                <input type="text" id="ruaVendedor" value={rua} onChange={(e) => setRua(e.target.value)} placeholder="Rua" required />
            </div>
            {erros.numero && <p className="message-error">{erros.numero}</p>}
            <div className="form-group">
                <label htmlFor="numeroVendedor">Número</label>
                <input type="text" id="numeroVendedor" value={numero} onChange={(e) => setNumero(e.target.value)} placeholder="Número" required />
            </div>
            <div className="form-group">
                <label htmlFor="complementoVendedor">Complemento</label>
                <input type="text" id="complementoVendedor" value={complemento} onChange={(e) => setComplemento(e.target.value)} placeholder="Apto, Bloco, etc." />
            </div>
            {erros.bairro && <p className="message-error">{erros.bairro}</p>}
            <div className="form-group">
                <label htmlFor="bairroVendedor">Bairro</label>
                <input type="text" id="bairroVendedor" value={bairro} onChange={(e) => setBairro(e.target.value)} placeholder="Bairro" required />
            </div>
            {erros.cidade && <p className="message-error">{erros.cidade}</p>}
            <div className="form-group">
                <label htmlFor="cidadeVendedor">Cidade</label>
                <input type="text" id="cidadeVendedor" value={cidade} onChange={(e) => setCidade(e.target.value)} placeholder="Cidade" required />
            </div>
            {erros.estado && <p className="message-error">{erros.estado}</p>}
            <div className="form-group">
                <label htmlFor="estadoVendedor">Estado (UF)</label>
                <input type="text" id="estadoVendedor" value={estado} onChange={(e) => setEstado(e.target.value)} placeholder="Estado (UF)" required maxLength={2} />
            </div>
            {erros.cep && <p className="message-error">{erros.cep}</p>}
            <div className="form-group">
                <label htmlFor="cepVendedor">CEP</label>
                <input type="text" id="cepVendedor" value={cep} onChange={(e) => setCep(e.target.value)} placeholder="CEP" required />
            </div>

            <div className="form-group">
                <label htmlFor="telefoneVendedor">Telefone da Loja</label>
                <input type="text" id="telefoneVendedor" value={telefone} onChange={(e) => setTelefone(e.target.value)} placeholder="Telefone da Loja" />
            </div>
            <div className="form-group">
                <label htmlFor="fundacao">Data de Fundação</label>
                <input type="date" id="fundacao" value={fundacao} onChange={(e) => setFundacao(e.target.value)} placeholder="Data de Fundação" />
            </div>
            <div className="form-group">
                <label htmlFor="horarioFuncionamento">Horário de Funcionamento</label>
                <input type="text" id="horarioFuncionamento" value={horarioFuncionamento} onChange={(e) => setHorarioFuncionamento(e.target.value)} placeholder="Horário de Funcionamento" />
            </div>
            <div className="form-group">
                <label htmlFor="nomeResponsavel">Nome do Responsável</label>
                <input type="text" id="nomeResponsavel" value={nomeResponsavel} onChange={(e) => setNomeResponsavel(e.target.value)} placeholder="Nome do Responsável" />
            </div>
            <div className="form-group">
                <label htmlFor="cpfResponsavel">CPF do Responsável</label>
                <input type="text" id="cpfResponsavel" value={cpfResponsavel} onChange={(e) => setCpfResponsavel(e.target.value)} placeholder="CPF do Responsável" />
            </div>
            <div className="form-group">
                <label htmlFor="breveDescricaoLoja">Breve Descrição da Loja</label>
                <textarea id="breveDescricaoLoja" value={breveDescricaoLoja} onChange={(e) => setBreveDescricaoLoja(e.target.value)} placeholder="Breve Descrição da Loja"></textarea>
            </div>
            <div className="form-group">
                <label htmlFor="logotipoLoja">URL do Logotipo da Loja</label>
                <input type="text" id="logotipoLoja" value={logotipoLoja} onChange={(e) => setLogotipoLoja(e.target.value)} placeholder="URL do Logotipo da Loja" />
            </div>
            <div className="form-group">
                <label htmlFor="siteRedesSociais">Website/Redes Sociais</label>
                <input type="text" id="siteRedesSociais" value={siteRedesSociais} onChange={(e) => setsiteRedesSociais(e.target.value)} placeholder="Website/Redes Sociais" />
            </div>

            {erros.idCategoriaLoja && <p className="message-error">{erros.idCategoriaLoja}</p>}
            <div className="form-group">
                <label htmlFor="idCategoriaLoja">Categoria da Loja:</label>
                <select id="idCategoriaLoja" value={idCategoriaLoja} onChange={(e) => setIdCategoriaLoja(e.target.value)} required>
                    <option value="">Selecione uma categoria</option>
                    {categorias.map(cat => (
                        <option key={cat.id} value={cat.id}>
                            {cat.nome}
                        </option>
                    ))}
                </select>
            </div>
            {descricaoCategoria && <p className="text" style={{ fontSize: '0.9em', color: '#666' }}>{descricaoCategoria}</p>}

            <button type="submit" className="btn btn-primary">Salvar e Continuar</button>
        </form>
    );
};

const CompletarPerfil = () => {
    const navegar = useNavigate();
    const localizacao = useLocation();
    const [erro, setErro] = useState(null);
    const { atualizarStatusPerfil } = useContext(AuthContext);

    const { idUsuario, tipoUsuario, email } = localizacao.state || {};

    if (!idUsuario || !tipoUsuario) {
        return <p className="message-error">Erro: Dados de usuário não encontrados. Por favor, <Link to="/cadastro">tente se cadastrar novamente</Link>.</p>;
    }

    const aoEnviarPerfil = async (dadosPerfil) => {
        try {
            // 1. Obter o perfil existente para pegar o ID do Cliente/Vendedor
            const perfilResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/perfil/`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}` // Assumindo que o token está no localStorage
                }
            });
            const perfilExistente = perfilResponse.data;
            console.log('Perfil Response Data:', perfilExistente);
            let perfilId;
            let endpoint;

            if (tipoUsuario === 'Cliente') {
                perfilId = perfilExistente.usuario.id; // O ID do Cliente
                endpoint = `${process.env.REACT_APP_API_URL}/api/clientes/${perfilId}/`;
            } else { // Vendedor
                perfilId = perfilExistente.usuario.id; // O ID do Vendedor
                endpoint = `${process.env.REACT_APP_API_URL}/api/vendedores/${perfilId}/`;
            }
            console.log('Extracted perfilId:', perfilId);

            // 2. Mapear dados do frontend para o formato do Django
            let dadosParaDjango = {};
            if (tipoUsuario === 'Cliente') {
                dadosParaDjango = {
                    nome: dadosPerfil.nome,
                    cpf: dadosPerfil.cpf,
                    telefone: dadosPerfil.telefone,
                    data_nascimento: dadosPerfil.dataNascimento,
                    endereco: dadosPerfil.endereco // Endereço já está no formato correto
                };
            } else { // Vendedor
                dadosParaDjango = {
                    nome_loja: dadosPerfil.nomeLoja,
                    cnpj: dadosPerfil.cnpj,
                    telefone: dadosPerfil.telefone,
                    fundacao: dadosPerfil.fundacao,
                    horario_funcionamento: dadosPerfil.horarioFuncionamento,
                    nome_responsavel: dadosPerfil.nomeResponsavel,
                    cpf_responsavel: dadosPerfil.cpfResponsavel,
                    breve_descricao_loja: dadosPerfil.breveDescricaoLoja,
                    logotipo_loja: dadosPerfil.logotipoLoja,
                    site_redes_sociais: dadosPerfil.siteRedesSociais || '',
                    categoria_loja: dadosPerfil.idCategoriaLoja, // ID da categoria
                    endereco: dadosPerfil.endereco // Endereço já está no formato correto
                };
            }

            // 3. Enviar requisição PUT para atualizar o perfil
            const response = await axios.put(endpoint, dadosParaDjango, {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            });

            if (response.status === 200) { // HTTP 200 OK para PUT bem-sucedido
                // Após salvar o perfil, o usuário pode ser considerado com perfil completo
                atualizarStatusPerfil(true); 

                // Verificar se o usuário está ativo (e-mail verificado)
                // Para isso, precisamos do status atualizado do usuário, que pode vir do token ou de uma nova chamada de perfil
                // Por simplicidade, vamos assumir que o token já tem essa informação ou que o frontend fará a checagem
                // A lógica de redirecionamento baseada em usuarioAtivo deve ser feita no frontend após o login ou em um contexto global

                // Redirecionar para a tela de verificação de e-mail se não estiver ativo
                // Ou para o painel se já estiver ativo
                const token = localStorage.getItem('token');
                if (token) {
                    const decodedToken = jwtDecode(token);
                    if (decodedToken.email_verificado) { // Assumindo que o token JWT tem a claim email_verificado
                        navegar('/painel'); // Ou para o dashboard específico
                    } else {
                        navegar('/verificar-email', { state: { email: email } });
                    }
                } else {
                    // Se não há token, algo deu errado, redirecionar para login
                    navegar('/login');
                }

            } else {
                setErro(response.data.message || 'Ocorreu um erro ao salvar o perfil.');
            }
        } catch (error) {
            console.error('Erro ao salvar perfil:', error.response ? error.response.data : error.message);
            setErro(error.response?.data?.message || 'Falha ao conectar com o servidor ou erro ao salvar perfil.');
        }
    };

    return (
        <div style={{ position: 'relative', minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px' }}>
            <div className="dashboard-background-layer"></div>
            <h2 className="dashboard-title">Quase lá!</h2>
            <p className="text" style={{ textAlign: 'center', marginBottom: '20px' }}>Por favor, complete as informações abaixo para finalizar seu cadastro.</p>
            {erro && <p className="message-error">{erro}</p>}

            {tipoUsuario === 'Cliente' ? (
                <FormularioCliente idUsuario={idUsuario} aoEnviar={aoEnviarPerfil} />
            ) : (
                <FormularioVendedor idUsuario={idUsuario} aoEnviar={aoEnviarPerfil} />
            )}
        </div>
    );
};

export default CompletarPerfil;
