import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const VerificarEmail = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const [codigo, setCodigo] = useState('');
    const [erro, setErro] = useState(null);
    const [mensagem, setMensagem] = useState('');

    // Pega o email do estado da navegação
    const { email } = location.state || {};

    if (!email) {
        return <p>Erro: Email não encontrado. Por favor, <a href="/cadastro">comece o processo de cadastro novamente</a>.</p>;
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErro(null);
        setMensagem('');

        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/autenticacao/verify-email`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, tokenConfirmacao: codigo }),
            });

            const data = await response.json();

            if (response.ok) {
                setMensagem('Conta ativada com sucesso! Redirecionando para o login...');
                setTimeout(() => {
                    navigate('/login');
                }, 3000); // Espera 3 segundos antes de redirecionar
            } else {
                setErro(data.message || 'Ocorreu um erro ao verificar o código.');
            }
        } catch (error) {
            setErro('Falha ao conectar com o servidor.');
        }
    };

    const handleResendCode = async () => {
        setErro(null);
        setMensagem('');
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/autenticacao/resend-verification`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email }),
            });
            const data = await response.json();
            if (response.ok) {
                setMensagem(data.message + ' Por favor, verifique seu email e digite 123456.');
            } else {
                setErro(data.message || 'Erro ao reenviar código.');
            }
        } catch (error) {
            setErro('Falha ao conectar com o servidor.');
        }
    };

    return (
        <div>
            <h2>Verifique seu Email</h2>
            <p>Enviamos um código de confirmação para <strong>{email}</strong>.</p>
            <p>Para fins de desenvolvimento, o código é <strong>123456</strong>.</p>

            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    value={codigo} 
                    onChange={(e) => setCodigo(e.target.value)} 
                    placeholder="Digite o código de 6 dígitos" 
                    required 
                />
                <button type="submit">Verificar e Ativar Conta</button>
            </form>

            {erro && <p style={{ color: 'red' }}>{erro}</p>}
            {mensagem && <p style={{ color: 'green' }}>{mensagem}</p>}

            <button type="button" onClick={handleResendCode} style={{ marginTop: '10px' }}>
                Reenviar Código
            </button>
        </div>
    );
};

export default VerificarEmail;
