import React, { createContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [usuario, setUsuario] = useState(null);
    const [token, setToken] = useState(localStorage.getItem('token'));
    const [email, setEmail] = useState(localStorage.getItem('email'));
    const [emailVerificado, setEmailVerificado] = useState(() => {
        const verificadoSalvo = localStorage.getItem('emailVerificado');
        return verificadoSalvo ? JSON.parse(verificadoSalvo) : false;
    });

    useEffect(() => {
        if (token) {
            try {
                const tokenDecodificado = jwtDecode(token);
                if (tokenDecodificado.exp * 1000 < Date.now()) {
                    logout();
                } else {
                    setUsuario({ id: tokenDecodificado.id, tipoUsuario: tokenDecodificado.tipo_usuario }); // Use tipo_usuario
                    setEmailVerificado(tokenDecodificado.email_verificado); // Set email_verificado
                }
            } catch (error) {
                console.error("Token inválido", error);
                logout();
            }
        }
    }, [token]);

    const login = (novoToken, statusPerfil, userEmail, isEmailVerified) => {
        localStorage.setItem('token', novoToken);
        localStorage.setItem('perfilCompleto', JSON.stringify(statusPerfil));
        localStorage.setItem('email', userEmail);
        localStorage.setItem('emailVerificado', JSON.stringify(isEmailVerified));
        setToken(novoToken);
        setPerfilCompleto(statusPerfil);
        setEmail(userEmail);
        setEmailVerificado(isEmailVerified);
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('perfilCompleto');
        localStorage.removeItem('email');
        localStorage.removeItem('emailVerificado');
        setToken(null);
        setUsuario(null);
        setPerfilCompleto(false);
        setEmail(null);
        setEmailVerificado(false);
    };

    const atualizarStatusPerfil = (status) => {
        localStorage.setItem('perfilCompleto', JSON.stringify(status));
        setPerfilCompleto(status);
    };

    const valorDoContexto = {
        usuario,
        token,
        email,
        perfilCompleto,
        emailVerificado, // Expose emailVerificado
        login,
        logout,
        atualizarStatusPerfil,
    };

    return <AuthContext.Provider value={valorDoContexto}>{children}</AuthContext.Provider>;
};
