import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';


const EditarPerfilVendedor = () => {
    const { token, usuario } = useContext(AuthContext);
    const navigate = useNavigate();
    const [perfil, setPerfil] = useState({
                nomeLoja: '',
        cnpj: '',
        endereco: {
            logradouro: '',
            numero: '',
            complemento: '',
            bairro: '',
            cidade: '',
            estado: '',
            cep: '',
        },
        telefone: '',
        dataFundacao: '',
        horarioFuncionamento: '',
        nomeResponsavel: '',
        cpfResponsavel: '',
        breveDescricaoLoja: '',
        logotipoLoja: '',
        siteRedesSociais: '',
        categoriaLoja: '',
    });
    const [loading, setLoading] = useState(true);
    const [notification, setNotification] = useState({ message: '', type: '' });
    const [categorias, setCategorias] = useState([]);

    const showNotification = (message, type) => {
        setNotification({ message, type });
        setTimeout(() => {
            setNotification({ message: '', type: '' });
        }, 5000);
    };

    useEffect(() => {
        const fetchPerfilAndCategories = async () => {

            if (!token || usuario?.tipoUsuario !== 'Vendedor') {
                // Navega para não autorizado se a condição não for atendida
                navigate('/nao-autorizado');
                return; // Impede a execução do restante da função
            }

            try {
                // Fetch seller profile
                const perfilResponse = await axios.get('/api/perfil/', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setPerfil(perfilResponse.data);

                // Fetch categories
                const categoriasResponse = await axios.get('/api/categorias/', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setCategorias(categoriasResponse.data);

            } catch (err) {
                showNotification('Falha ao carregar dados do perfil ou categorias.', 'error');
                console.error(err);
                // Adicionar uma verificação de erro para garantir que a tela não fique em branco
                setPerfil(null); 
            }
            setLoading(false);
        };

//        if (token && usuario?.tipoUsuario === 'vendedor') {
//            fetchPerfilAndCategories();
//        } else if (!usuario || usuario?.tipoUsuario !== 'vendedor') {
//            navigate('/nao-autorizado');
//        }
    // Chama a função de busca
        fetchPerfilAndCategories();
    }, [token, usuario, navigate])
    const handleChange = (e) => {
        setPerfil({ ...perfil, [e.target.name]: e.target.value });
    };

const handleSubmit = async (e) => {
    e.preventDefault();
    try {
        // Acessa o ID do vendedor do objeto `usuario` do contexto
        const idVendedor = usuario.ID_Vendedor; // <--- Importante: O objeto `usuario` precisa ter a propriedade `ID_Vendedor`

        // Constrói a URL para a rota correta do Django REST Framework
        // Exemplo: /api/vendedores/1/ (para atualizar o vendedor com ID 1)
        const updateUrl = `/api/vendedores/${idVendedor}/`; 

        await axios.put(updateUrl, perfil, {
            headers: { Authorization: `Bearer ${token}` }
        });

        showNotification('Perfil atualizado com sucesso!', 'success');
    } catch (err) {
        showNotification('Falha ao atualizar perfil.', 'error');
        console.error(err);
    }
};


    if (loading) return <div>Carregando perfil...</div>;

    // Se o perfil for nulo após o carregamento (devido a um erro), exibe uma mensagem de fallback.
    if (!perfil) {
        return (
            <div className="container" style={{ textAlign: 'center' }}>
                <h1>Erro ao carregar o perfil.</h1>
                <p>Não foi possível encontrar suas informações. Por favor, tente novamente mais tarde.</p>
            </div>
        );
    }

    return (
        <div className="container">
            {notification.message && (
                <div className={`notification ${notification.type}`}>
                    {notification.message}
                </div>
            )}
            <h1>Editar Perfil do Vendedor</h1>
            <form onSubmit={handleSubmit} className="form-container">
                <div className="form-group">
                    <label>Nome da Loja:</label>
                    <input type="text" name="nomeLoja" value={perfil.nomeLoja || ''} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>CNPJ:</label>
                    <input type="text" name="cnpj" value={perfil.cnpj || ''} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>Endereço:</label>
                    <input type="text" name="endereco.logradouro" value={perfil.endereco.logradouro || ''} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>Telefone:</label>
                    <input type="text" name="telefone" value={perfil.telefone || ''} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>Data de Fundação:</label>
                    <input type="date" name="Fundacao" value={perfil.Fundacao ? perfil.Fundacao.split('T')[0] : ''} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>Horário de Funcionamento:</label>
                    <input type="text" name="HorarioFuncionamento" value={perfil.HorarioFuncionamento || ''} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>Nome do Responsável:</label>
                    <input type="text" name="NomeResponsavel" value={perfil.NomeResponsavel || ''} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>CPF do Responsável:</label>
                    <input type="text" name="CPF_Responsavel" value={perfil.CPF_Responsavel || ''} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>Breve Descrição da Loja:</label>
                    <textarea name="BreveDescricaoLoja" value={perfil.BreveDescricaoLoja || ''} onChange={handleChange}></textarea>
                </div>
                <div className="form-group">
                    <label>Logotipo da Loja (URL):</label>
                    <input type="text" name="logotipoLoja" value={perfil.logotipoLoja || ''} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>Website/Redes Sociais:</label>
                    <input type="text" name="WebsiteRedesSociais" value={perfil.WebsiteRedesSociais || ''} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label>Categoria da Loja:</label>
                    <select name="ID_CategoriaLoja" value={perfil.ID_CategoriaLoja || ''} onChange={handleChange} required>
                        <option value="">Selecione uma categoria</option>
                        {categorias.map(cat => (
                            <option key={cat.ID_CategoriaLoja} value={cat.ID_CategoriaLoja}>
                                {cat.NomeCategoria}
                            </option>
                        ))}
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Salvar Alterações</button>
            </form>
        </div>
    );
};

export default EditarPerfilVendedor;
