import React, { useContext } from 'react';
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Navigate
} from 'react-router-dom';

// Importa o Contexto e os Componentes de Rota
import { AuthProvider, AuthContext } from './context/AuthContext';
import ProtectedRoute from './components/ProtectedRoute';

// Importa as páginas (crie estes arquivos se ainda não existirem)
import Login from './pages/Login';
import Register from './pages/Register';
import Home from './pages/Home';

// Componentes de página para demonstração. Substitua pelos seus componentes reais.
const SellerDashboard = () => <h2>Painel do Vendedor</h2>;
const ClientSearch = () => <h2>Busca de Produtos para Clientes</h2>;
const Unauthorized = () => <h2>Acesso Não Autorizado</h2>;

// Este componente especial redireciona o usuário para o painel correto após o login.
const DashboardRedirect = () => {
    const { user } = useContext(AuthContext);

    // Mostra uma mensagem de carregamento enquanto o usuário é verificado
    if (!user) {
        return <div>Carregando...</div>;
    }

    // Redireciona com base no tipo de usuário
    if (user.userType === 'vendedor') {
        return <Navigate to="/vendedor/painel" replace />;
    }
    if (user.userType === 'cliente') {
        return <Navigate to="/cliente/busca" replace />;
    }

    // Se o tipo de usuário for inválido, volta para o login
    return <Navigate to="/login" replace />;
};

import Layout from './components/Layout';

function App() {
    return (
        <AuthProvider>
            <Router>
                <Layout>
                    <Routes>
                        {/* --- Rotas Públicas --- */}
                        <Route path="/" element={<Home />} />
                        <Route path="/login" element={<Login />} />
                        <Route path="/cadastro" element={<Register />} />
                        <Route path="/nao-autorizado" element={<Unauthorized />} />

                        {/* --- Rotas Protegidas --- */}
                        <Route path="/dashboard" element={<ProtectedRoute><DashboardRedirect /></ProtectedRoute>} />

                        {/* Rotas específicas para VENDEDOR */}
                        <Route path="/vendedor/painel" element={<ProtectedRoute allowedRoles={['vendedor']}><SellerDashboard /></ProtectedRoute>} />

                        {/* Rotas específicas para CLIENTE */}
                        <Route path="/cliente/busca" element={<ProtectedRoute allowedRoles={['cliente']}><ClientSearch /></ProtectedRoute>} />

                    </Routes>
                </Layout>
            </Router>
        </AuthProvider>
    );
}

export default App;
