import React, { useEffect, useContext } from 'react';
import { useLocation } from 'react-router-dom';
import BarraNavegacao from './BarraNavegacao';
import Footer from './Footer';
import { AuthContext } from '../context/AuthContext'; // Importar o AuthContext

const Estrutura = ({ children }) => {
    const location = useLocation();
    const { user } = useContext(AuthContext); // Obter o estado do usuário

    useEffect(() => {
        // Adiciona a classe de fundo se o usuário estiver logado
        if (user) {
            document.body.classList.add('unicorn-background');
        } else {
            document.body.classList.remove('unicorn-background');
        }

        // Limpa a classe quando o componente é desmontado
        return () => {
            document.body.classList.remove('unicorn-background');
        };
    }, [user]); // O efeito depende do estado do usuário

    useEffect(() => {
        window.scrollTo(0, 0);
    }, [location.pathname]);

    return (
        <>
            <BarraNavegacao />
            <main className="apresentacao">
                {children}
            </main>
            <Footer />
        </>
    );
};

export default Estrutura;
