import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';


const DashboardVendedor = () => {
  const navigate = useNavigate();
  const { token, user } = useContext(AuthContext);
  const userName = user?.nomeLoja || 'Usu√°rio'; // Assumindo que o nome da loja est√° em user.nomeLoja
  const [avaliacoes, setAvaliacoes] = useState([]);
  const [loadingAvaliacoes, setLoadingAvaliacoes] = useState(true);
  const [errorAvaliacoes, setErrorAvaliacoes] = useState('');
  const [showSuggestionForm, setShowSuggestionForm] = useState(false);
  const [suggestionText, setSuggestionText] = useState('');
  const [suggestionStatus, setSuggestionStatus] = useState('');

  const handleSendSuggestion = async () => {
    setSuggestionStatus('');
    if (!suggestionText.trim()) {
      setSuggestionStatus('A sugest√£o n√£o pode ser vazia.');
      return;
    }
    try {
      await axios.post('/usuarios/suggestions', { suggestion: suggestionText }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuggestionStatus('Sugest√£o enviada com sucesso!');
      setSuggestionText('');
      setShowSuggestionForm(false);
    } catch (err) {
      setSuggestionStatus('Falha ao enviar sugest√£o.');
      console.error(err);
    }
  };

  const avaliacaoMedia = avaliacoes.length > 0
    ? (avaliacoes.reduce((acc, curr) => acc + curr.Nota, 0) / avaliacoes.length).toFixed(2)
    : 'N/A';



  useEffect(() => {
    const fetchAvaliacoes = async () => {
      try {
        const response = await axios.get('/usuarios/avaliacoes', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setAvaliacoes(response.data);
      } catch (err) {
        setErrorAvaliacoes('Falha ao buscar suas avalia√ß√µes.');
        console.error(err);
      }
      setLoadingAvaliacoes(false);
    };

    if (token) {
      fetchAvaliacoes();
    }
  }, [token]);

  // Dados de exemplo para a lista de produtos mais acessados
  const produtosMaisAcessados = [
    { id: 1, nome: 'Produto A', acessos: 150 },
    { id: 2, nome: 'Produto B', acessos: 120 },
    { id: 3, nome: 'Produto C', acessos: 95 },
  ];

  return (
    <div style={{ position: 'relative', minHeight: '100vh' }}>
      <div className="dashboard-background-layer"></div>
      <div className="dashboard-container">

        <h1 className="dashboard-title">Painel do Vendedor</h1>
        <p className="text" style={{ textAlign: 'center', marginBottom: '30px' }}>Bem-vindo, {userName}!</p>

        {/* Se√ß√£o de Gerenciamento de Produtos */}
        <div className="card-grid">
          <div className="card">
            <span className="card-icon">üì¶</span> {/* √çcone de pacote */}
            <h2 className="card-title">Gerenciamento de Produtos</h2>
            <p className="card-text">Adicione novos produtos ou visualize e edite os existentes.</p>
            <button onClick={() => navigate('/cadastrar-produto')} className="btn btn-primary">Cadastrar Novo Produto</button>
            <button onClick={() => navigate('/meus-produtos')} className="btn btn-secondary" style={{ marginTop: '10px' }}>Ver Meus Produtos</button>
          </div>

          {/* Se√ß√£o de An√°lise */}
          <div className="card">
            <span className="card-icon">üìà</span> {/* √çcone de gr√°fico */}
            <h2 className="card-title">Produtos Mais Acessados</h2>
            <p className="card-text">Acompanhe quais produtos est√£o gerando mais interesse.</p>
            <ul>
              {produtosMaisAcessados.map(produto => (
                <li key={produto.id} className="text" style={{ marginBottom: '5px' }}>
                  {produto.nome} - <strong>{produto.acessos} visualiza√ß√µes</strong>
                </li>
              ))}
            </ul>
          </div>

          {/* Se√ß√£o de Conta */}
          <div className="card">
            <span className="card-icon">‚öôÔ∏è</span> {/* √çcone de engrenagem */}
            <h2 className="card-title">Minha Conta</h2>
            <p className="card-text">Edite suas informa√ß√µes de perfil e dados da loja.</p>
            <button onClick={() => navigate('/editar-perfil-vendedor')} className="btn btn-secondary">Editar Perfil e Dados da Loja</button>
            <p className="text" style={{ fontSize: '0.8rem', marginTop: '10px' }}><small>Obs: A altera√ß√£o de dados como CNPJ requer aprova√ß√£o de um administrador.</small></p>
          </div>

          {/* Se√ß√£o de Avalia√ß√µes */}
          <div className="card">
            <span className="card-icon">‚≠ê</span> {/* √çcone de estrela */}
            <h2 className="card-title">Minhas Avalia√ß√µes</h2>
            {loadingAvaliacoes ? (
              <p>Carregando avalia√ß√µes...</p>
            ) : errorAvaliacoes ? (
              <p>{errorAvaliacoes}</p>
            ) : (
              <>
                <p className="card-text">Avalia√ß√£o M√©dia: <strong>{avaliacaoMedia}</strong></p>
                {avaliacoes.length === 0 ? (
                  <p>Voc√™ ainda n√£o possui avalia√ß√µes.</p>
                ) : (
                  <p>Voc√™ possui {avaliacoes.length} avalia√ß√£o(√µes).</p>
                )}
                <button 
                  onClick={() => navigate('/minhas-avaliacoes')}
                  className="btn btn-secondary"
                  style={{ marginTop: '10px' }}
                  disabled={avaliacoes.length === 0}
                >
                  Ver Todas as Avalia√ß√µes
                </button>
              </>
            )}
          </div>


        </div>
      </div>

      <div style={{ textAlign: 'center', marginTop: '30px' }}>
        <button onClick={() => navigate('/')} className="btn btn-secondary">Voltar ao In√≠cio</button>
      </div>

    </div>
  );
};

export default DashboardVendedor;
