import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import '../styles/Global.css';

const BarraNavegacao = () => {
    const { usuario, logout } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/');
    };

    return (
        <header className="cabecalho">
            <img src="/assets/logo.png" alt="Logo CDK Tech" style={{width: "80px", height: "auto", marginRight: "20px"}} />
            <nav className="cabecalho_menu">
                <Link className="cabecalho_menu_link" to="/">In√≠cio</Link>
                <Link className="cabecalho_menu_link" to="/produtos">Produtos</Link>
                {usuario ? (
                    <button onClick={handleLogout} className="cabecalho_menu_link" style={{ background: 'none', border: 'none', cursor: 'pointer' }}>
                        Sair
                    </button>
                ) : (
                    <>
                        <Link className="cabecalho_menu_link" to="/login">Login</Link>
                        <Link className="cabecalho_menu_link" to="/cadastro">Cadastrar</Link>
                    </>
                )}
            </nav>
        </header>
    );
};

export default BarraNavegacao;
