import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const CadastroProduto = () => {
  const { token } = useContext(AuthContext);
  const [todasSubcategorias, setTodasSubcategorias] = useState([]); // Novo estado para todas as subcategorias

  // Estado para informações gerais do produto
  const [nomeProduto, setNomeProduto] = useState('');
  const [descricao, setDescricao] = useState('');

  // Estado para busca e seleção de subcategoria
  const [buscaSubcategoria, setBuscaSubcategoria] = useState('');
  const [sugestoes, setSugestoes] = useState([]);
  const [subcategoriaSelecionada, setSubcategoriaSelecionada] = useState(null);

  // Estado para as variações do produto
  const [variacoes, setVariacoes] = useState([]);

  // Efeito para carregar as subcategorias do backend
  useEffect(() => {
    const fetchSubcategories = async () => {
      try {
        const response = await axios.get('/api/subcategories');
        setTodasSubcategorias(response.data.map(sub => ({ id: sub.ID_Subcategoria, nome: sub.NomeSubcategoria })));
      } catch (error) {
        console.error('Erro ao buscar subcategorias:', error);
        alert('Erro ao carregar subcategorias. Tente novamente.');
      }
    };
    fetchSubcategories();
  }, []);

  // Efeito para atualizar as sugestões de subcategoria
  useEffect(() => {
    if (buscaSubcategoria.length > 1) {
      const filtradas = todasSubcategorias.filter(sub =>
        sub.nome.toLowerCase().includes(buscaSubcategoria.toLowerCase())
      );
      setSugestoes(filtradas);
    } else {
      setSugestoes([]);
    }
  }, [buscaSubcategoria, todasSubcategorias]);

  const handleSelecionarSubcategoria = (sub) => {
    setSubcategoriaSelecionada(sub);
    setBuscaSubcategoria(sub.nome);
    setSugestoes([]);
    console.log('Subcategoria selecionada:', sub);
  };

  const handleAdicionarVariacao = () => {
    setVariacoes([
      ...variacoes,
      {
        id: Date.now(), // ID provisório
        nomeVariacao: '',
        valorVariacao: '',
        preco: '',
        quantidadeDisponivel: '',
        imagens: [],
      },
    ]);
  };

  const handleRemoverVariacao = (id) => {
    setVariacoes(variacoes.filter(v => v.id !== id));
  };

  const handleVariacaoChange = (id, campo, valor) => {
    const novasVariacoes = variacoes.map(v => {
      if (v.id === id) {
        return { ...v, [campo]: valor };
      }
      return v;
    });
    setVariacoes(novasVariacoes);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Subcategoria selecionada no submit:', subcategoriaSelecionada);
    if (!subcategoriaSelecionada) {
      alert('Por favor, selecione uma subcategoria válida.');
      return;
    }
    if (variacoes.length === 0) {
        alert('Adicione pelo menos uma variação para o produto.');
        return;
    }

    const dadosProduto = {
      nomeProduto,
      descricao,
      idSubcategoria: subcategoriaSelecionada.id,
      variacoes,
    };

    try {
      await axios.post('/api/produtos/completo', dadosProduto, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      alert('Produto cadastrado com sucesso!');
      // Limpar o formulário ou redirecionar
    } catch (error) {
      console.error('Erro ao cadastrar produto:', error);
      alert('Falha ao cadastrar o produto.');
    }
  };

  return (
    <div>
      <h1>Cadastro de Produto</h1>
      <form onSubmit={handleSubmit}>
        {/* Seção 1: Informações Gerais */}
        <fieldset>
          <legend>Informações Gerais do Produto</legend>
          <div>
            <label htmlFor="nomeProduto">Nome do Produto:</label>
            <input
              type="text"
              id="nomeProduto"
              value={nomeProduto}
              onChange={(e) => setNomeProduto(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="descricao">Descrição:</label>
            <textarea
              id="descricao"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="buscaSubcategoria">Buscar Subcategoria:</label>
            <input
              type="text"
              id="buscaSubcategoria"
              value={buscaSubcategoria}
              onChange={(e) => setBuscaSubcategoria(e.target.value)}
              required
            />
            {sugestoes.length > 0 && (
              <ul>
                {sugestoes.map(s => (
                  <li key={s.id} onClick={() => handleSelecionarSubcategoria(s)}>
                    {s.nome}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </fieldset>

        {/* Seção 2: Variações e Ofertas */}
        <fieldset>
          <legend>Variações e Ofertas</legend>
          {variacoes.map((variacao, index) => (
            <div key={variacao.id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px 0' }}>
              <h4>Variação {index + 1}</h4>
              <div>
                <label>Nome da Variação (ex: Marca, Sabor):</label>
                <input
                  type="text"
                  value={variacao.nomeVariacao}
                  onChange={(e) => handleVariacaoChange(variacao.id, 'nomeVariacao', e.target.value)}
                  required
                />
              </div>
              <div>
                <label>Valor da Variação (ex: Ninho, Morango):</label>
                <input
                  type="text"
                  value={variacao.valorVariacao}
                  onChange={(e) => handleVariacaoChange(variacao.id, 'valorVariacao', e.target.value)}
                  required
                />
              </div>
              <div>
                <label>Preço (R$):</label>
                <input
                  type="number"
                  step="0.01"
                  value={variacao.preco}
                  onChange={(e) => handleVariacaoChange(variacao.id, 'preco', e.target.value)}
                  required
                />
              </div>
              <div>
                <label>Quantidade em Estoque:</label>
                <input
                  type="number"
                  value={variacao.quantidadeDisponivel}
                  onChange={(e) => handleVariacaoChange(variacao.id, 'quantidadeDisponivel', e.target.value)}
                  required
                />
              </div>
              <div>
                <label>Imagens:</label>
                <input type="file" multiple />
              </div>
              <button type="button" onClick={() => handleRemoverVariacao(variacao.id)}>Remover Variação</button>
            </div>
          ))}
          <button type="button" onClick={handleAdicionarVariacao}>
            + Adicionar Variação
          </button>
        </fieldset>

        <hr />
        <button type="submit">Cadastrar Produto Completo</button>
      </form>
    </div>
  );
};

export default CadastroProduto;
