import React, { createContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [token, setToken] = useState(localStorage.getItem('token'));
    const [perfilCompleto, setPerfilCompleto] = useState(false); // Novo estado
    const [emailVerificado, setEmailVerificado] = useState(false); // Novo estado
    const [usuario, setUsuario] = useState(() => {
        const userSalvo = localStorage.getItem('usuario');
        try {
            return userSalvo ? JSON.parse(userSalvo) : null;
        } catch (e) {
            return null;
        }
    });

    useEffect(() => {
        const loadUserFromLocalStorage = () => {
            const userSalvo = localStorage.getItem('usuario');
            try {
                return userSalvo ? JSON.parse(userSalvo) : null;
            } catch (e) {
                console.error("Erro ao parsear usuário do localStorage", e);
                return null;
            }
        };

        if (token) {
            const loadedUser = loadUserFromLocalStorage();
            if (loadedUser) {
                setUsuario(loadedUser);
            }
            try {
                const tokenDecodificado = jwtDecode(token);
                if (tokenDecodificado.exp * 1000 < Date.now()) {
                    logout();
                }
            } catch (error) {
                console.error("Token inválido", error);
                logout();
            }
        } else {
            setUsuario(null); // Clear user if no token
        }
    }, [token]);

    const login = (novoToken, dadosUsuario) => {
        console.log('AuthContext - Dados do usuário recebidos na função login:', dadosUsuario);
        localStorage.setItem('token', novoToken);
        localStorage.setItem('usuario', JSON.stringify(dadosUsuario));
        setToken(novoToken);
        setUsuario(dadosUsuario);
        setPerfilCompleto(dadosUsuario.perfil_completo);
        setEmailVerificado(dadosUsuario.email_verificado);
        console.log('AuthContext - Estado do usuário após setUsuario:', dadosUsuario);
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('usuario');
        setToken(null);
        setUsuario(null);
        setPerfilCompleto(false);
        setEmailVerificado(false);
    };

    const atualizarStatusPerfil = (status) => {
        if (usuario) {
            const usuarioAtualizado = { ...usuario, perfil_completo: status };
            setUsuario(usuarioAtualizado);
            localStorage.setItem('usuario', JSON.stringify(usuarioAtualizado));
            setPerfilCompleto(status);
        }
    };

    // Fornecer os valores derivados do objeto 'usuario' para manter a compatibilidade
    // com os componentes que ainda não foram refatorados.
    const valorDoContexto = {
        token,
        usuario,
        email: usuario?.email,
        perfilCompleto: usuario?.perfil_completo,
        emailVerificado: usuario?.email_verificado,
        login,
        logout,
        atualizarStatusPerfil,
    };

    return <AuthContext.Provider value={valorDoContexto}>{children}</AuthContext.Provider>;
};
