import React, { useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';


const IndicarVendedor = () => {
    const { token } = useContext(AuthContext);
    const [indicacao, setIndicacao] = useState({
        nomeIndicado: '',
        emailIndicado: '',
        telefoneIndicado: '',
        mensagem: '',
    });
    const [notification, setNotification] = useState({ message: '', type: '' });

    const showNotification = (message, type) => {
        setNotification({ message, type });
        setTimeout(() => {
            setNotification({ message: '', type: '' });
        }, 3000);
    };

    const handleIndicacaoChange = (e) => {
        setIndicacao({ ...indicacao, [e.target.name]: e.target.value });
    };

    const handleIndicacaoSubmit = async (e) => {
        e.preventDefault();
        setIndicacaoStatus('');
        try {
            await axios.post('/usuarios/indicate-seller', indicacao, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setIndicacaoStatus('Indicação enviada com sucesso!');
            setIndicacao({
                nomeIndicado: '',
                emailIndicado: '',
                telefoneIndicado: '',
                mensagem: '',
            });
        } catch (err) {
            setIndicacaoStatus('Falha ao enviar indicação.');
            console.error(err);
        }
    };

    return (
        <div className="container">
            <h1>Indicar Novo Vendedor</h1>
            <p>Conhece alguém que deveria vender aqui? Indique-o!</p>
            <form onSubmit={handleIndicacaoSubmit} className="form-container">
                <div className="form-group">
                    <label>Nome do Indicado:</label>
                    <input
                        type="text"
                        name="nomeIndicado"
                        value={indicacao.nomeIndicado}
                        onChange={handleIndicacaoChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Email do Indicado:</label>
                    <input
                        type="email"
                        name="emailIndicado"
                        value={indicacao.emailIndicado}
                        onChange={handleIndicacaoChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Telefone do Indicado (opcional):</label>
                    <input
                        type="text"
                        name="telefoneIndicado"
                        value={indicacao.telefoneIndicado}
                        onChange={handleIndicacaoChange}
                    />
                </div>
                <div className="form-group">
                    <label>Mensagem (opcional):</label>
                    <textarea
                        name="mensagem"
                        value={indicacao.mensagem}
                        onChange={handleIndicacaoChange}
                    ></textarea>
                </div>
                <button type="submit" className="btn btn-primary">Enviar Indicação</button>
            </form>
            {indicacaoStatus && <p className="message-info">{indicacaoStatus}</p>}
        </div>
    );
};

export default IndicarVendedor;
