import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const FormularioLogin = () => {
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [erro, setErro] = useState('');
    const [mensagemSucesso, setMensagemSucesso] = useState('');

    const { login } = useContext(AuthContext);
    const navegar = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErro('');
        setMensagemSucesso('');

        try {
            const response = await axios.post('/autenticacao/login', { email, senha });

            login(response.data.token, response.data.perfilCompleto, email);

            if (!response.data.perfilCompleto) {
                navegar('/completar-perfil', { state: { idUsuario: response.data.idUsuario, tipoUsuario: response.data.tipoUsuario, email: email } });
            } else {
                navegar('/painel'); 
            } 

        } catch (err) {
            const mensagem = err.response?.data?.message || 'Falha no login. Verifique suas credenciais.';
            setErro(mensagem);

            if (mensagem.includes('Conta inativa')) {
                navegar('/verificar-email', { state: { email: email } });
            }
        }
    };

    return (
        <form onSubmit={handleSubmit} className="form-container">
            <h2>Login</h2>
            {erro && <p className="message-error">{erro}</p>}
            {mensagemSucesso && <p className="message-success">{mensagemSucesso}</p>}
            <div className="form-group">
                <label>Email:</label>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label>Senha:</label>
                <input
                    type="password"
                    value={senha}
                    onChange={(e) => setSenha(e.target.value)}
                    onKeyPress={(e) => { if (e.key === 'Enter') handleSubmit(e); }}
                    required
                />
            </div>
            <div className="form-actions">
                <button type="submit" className="btn btn-primary">Entrar</button>
            </div>
        </form>
    );
};

export default FormularioLogin;
