import React, { useState } from 'react';

const FormularioCadastro = () => {
    const [nome, setNome] = useState('');
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [tipoUsuario, setTipoUsuario] = useState('cliente'); // 'cliente' ou 'vendedor'
    const [erro, setErro] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErro(null);

        const response = await fetch('/api/auth/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ nome, email, senha, tipoUsuario }),
        });

        if (response.ok) {
            // Lidar com o cadastro bem-sucedido (ex: redirecionar para o login)
        } else {
            const dadosErro = await response.json();
            setErro(dadosErro.message);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Cadastrar</h2>
            {erro && <p style={{ color: 'red' }}>{erro}</p>}
            <div>
                <label>Nome:</label>
                <input
                    type="text"
                    value={nome}
                    onChange={(e) => setNome(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Email:</label>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Senha:</label>
                <input
                    type="password"
                    value={senha}
                    onChange={(e) => setSenha(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Tipo de Usu√°rio:</label>
                <select value={tipoUsuario} onChange={(e) => setTipoUsuario(e.target.value)}>
                    <option value="cliente">Cliente</option>
                    <option value="vendedor">Vendedor</option>
                </select>
            </div>
            <button type="submit">Cadastrar</button>
        </form>
    );
};

export default FormularioCadastro;
