            {erros.cnpj && <p style={{ color: 'red' }}>{erros.cnpj}</p>}
            <input type="text" value={cnpj} onChange={(e) => setCnpj(e.target.value)} placeholder="CNPJ" />

            <h4>Endereço</h4>
            {erros.rua && <p style={{ color: 'red' }}>{erros.rua}</p>}
            <input type="text" value={rua} onChange={(e) => setRua(e.target.value)} placeholder="Rua" required />
            {erros.numero && <p style={{ color: 'red' }}>{erros.numero}</p>}
            <input type="text" value={numero} onChange={(e) => setNumero(e.target.value)} placeholder="Número" required />
            {erros.bairro && <p style={{ color: 'red' }}>{erros.bairro}</p>}
            <input type="text" value={bairro} onChange={(e) => setBairro(e.target.value)} placeholder="Bairro" required />
            {erros.cidade && <p style={{ color: 'red' }}>{erros.cidade}</p>}
            <input type="text" value={cidade} onChange={(e) => setCidade(e.target.value)} placeholder="Cidade" required />
            {erros.estado && <p style={{ color: 'red' }}>{erros.estado}</p>}
            <input type="text" value={estado} onChange={(e) => setEstado(e.target.value)} placeholder="Estado (UF)" required maxLength={2} />

            <input type="text" value={telefone} onChange={(e) => setTelefone(e.target.value)} placeholder="Telefone da Loja" />
            <input type="date" value={fundacao} onChange={(e) => setFundacao(e.target.value)} placeholder="Data de Fundação" />
            <input type="text" value={horarioFuncionamento} onChange={(e) => setHorarioFuncionamento(e.target.value)} placeholder="Horário de Funcionamento" />
            <input type="text" value={nomeResponsavel} onChange={(e) => setNomeResponsavel(e.target.value)} placeholder="Nome do Responsável" />
            <input type="text" value={cpfResponsavel} onChange={(e) => setCpfResponsavel(e.target.value)} placeholder="CPF do Responsável" />
            <textarea value={breveDescricaoLoja} onChange={(e) => setBreveDescricaoLoja(e.target.value)} placeholder="Breve Descrição da Loja"></textarea>
