import React, { useState, useEffect, useContext, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const MeusProdutos = () => {
    const navigate = useNavigate();
    const [produtos, setProdutos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const { token } = useContext(AuthContext);
    const [categorias, setCategorias] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [editingProduct, setEditingProduct] = useState(null);
    const [notification, setNotification] = useState({ message: '', type: '' });

    const showNotification = (message, type) => {
        setNotification({ message, type });
        setTimeout(() => {
            setNotification({ message: '', type: '' });
        }, 3000); // A notificação desaparecerá após 3 segundos
    };

    const fetchProdutos = useCallback(async () => {
        try {
            const url = selectedCategory 
                ? `/produtos/meus-produtos?idCategoria=${selectedCategory}` 
                : '/produtos/meus-produtos';
            const response = await axios.get(url, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setProdutos(response.data);
        } catch (err) {
            setError('Falha ao buscar produtos.');
        }
        setLoading(false);
    }, [token, selectedCategory, setProdutos, setError, setLoading]);

    useEffect(() => {
        const fetchCategorias = async () => {
            try {
                const response = await axios.get('/categories');
                setCategorias(response.data);
            } catch (err) {
                // Silently fail on category fetch error
            }
        };

        if (token) {
            fetchProdutos();
            fetchCategorias();
        }
    }, [token, selectedCategory, fetchProdutos]);

    const handleEditClick = (product) => {
        setEditingProduct({ ...product });
        setEditSuccess('');
        setEditError('');
    };

    const handleCancelEdit = () => {
        setEditingProduct(null);
        setEditSuccess('');
        setEditError('');
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditingProduct(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSaveClick = async () => {
        if (!editingProduct) return;

        try {
            await axios.put(`/produtos/${editingProduct.ID_Produto}`, {
                idVariacao: editingProduct.ID_Variacao,
                NomeProduto: editingProduct.NomeProduto,
                Descricao: editingProduct.Descricao,
                NomeVariacao: editingProduct.NomeVariacao,
                ValorVariacao: editingProduct.ValorVariacao,
                Preco: editingProduct.Preco,
                QuantidadeDisponivel: editingProduct.QuantidadeDisponivel,
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            showNotification('Produto atualizado com sucesso!', 'success');
            setEditingProduct(null);
            fetchProdutos(); // Refresh the product list
        } catch (err) {
            showNotification('Falha ao atualizar produto.', 'error');
        }
    };

    const handleDeleteClick = async (idVariacao) => {
        if (window.confirm('Tem certeza que deseja excluir esta oferta de produto?')) {
            try {
                await axios.delete(`/produtos/${idVariacao}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                showNotification('Oferta de produto excluída com sucesso!', 'success');
                fetchProdutos(); // Atualiza a lista de produtos
            } catch (err) {
                showNotification('Falha ao excluir a oferta de produto.', 'error');
                console.error(err);
            }
        }
    };

    if (loading) return <div>Carregando...</div>;
    if (error) return <div>{error}</div>;

    return (
        <>
            {notification.message && (
                <div className={`notification ${notification.type}`}>
                    {notification.message}
                </div>
            )}

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
                <h1 className="dashboard-title">Meus Produtos</h1>
                <button onClick={() => navigate('/cadastrar-produto')} className="btn btn-primary">Cadastrar Novo Produto</button>
            </div>

            <div className="form-group" style={{ maxWidth: '300px', margin: '20px auto' }}>
                <label htmlFor="categoryFilter">Filtrar por Categoria:</label>
                <select 
                    id="categoryFilter"
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    className="form-control"
                >
                    <option value="">Todas as Categorias</option>
                    {categorias.map(cat => (
                        <option key={cat.ID_CategoriaLoja} value={cat.ID_CategoriaLoja}>
                            {cat.NomeCategoria}
                        </option>
                    ))}
                </select>
            </div>

            {produtos.length === 0 ? (
                <p style={{ textAlign: 'center' }}>Você ainda não cadastrou nenhum produto ou não há produtos nesta categoria.</p>
            ) : (
                <div className="card-grid">
                    {editSuccess && <p className="message-success">{editSuccess}</p>}
                    {editError && <p className="message-error">{editError}</p>}
                    {produtos.map(produto => (
                        <div key={produto.ID_Variacao} className="card">
                            {editingProduct && editingProduct.ID_Variacao === produto.ID_Variacao ? (
                                <> {/* Edit mode */}
                                    <div className="form-group">
                                        <label>Nome do Produto:</label>
                                        <input type="text" name="NomeProduto" value={editingProduct.NomeProduto || ''} onChange={handleChange} />
                                    </div>
                                    <div className="form-group">
                                        <label>Descrição:</label>
                                        <textarea name="Descricao" value={editingProduct.Descricao || ''} onChange={handleChange}></textarea>
                                    </div>
                                    <div className="form-group">
                                        <label>Nome da Variação:</label>
                                        <input type="text" name="NomeVariacao" value={editingProduct.NomeVariacao || ''} onChange={handleChange} />
                                    </div>
                                    <div className="form-group">
                                        <label>Valor da Variação:</label>
                                        <input type="text" name="ValorVariacao" value={editingProduct.ValorVariacao || ''} onChange={handleChange} />
                                    </div>
                                    <div className="form-group">
                                        <label>Preço:</label>
                                        <input type="number" name="Preco" value={editingProduct.Preco || ''} onChange={handleChange} step="0.01" />
                                    </div>
                                    <div className="form-group">
                                        <label>Estoque:</label>
                                        <input type="number" name="QuantidadeDisponivel" value={editingProduct.QuantidadeDisponivel || ''} onChange={handleChange} />
                                    </div>
                                    <div className="form-actions">
                                        <button onClick={handleSaveClick} className="btn btn-primary">Salvar</button>
                                        <button onClick={handleCancelEdit} className="btn btn-secondary">Cancelar</button>
                                        <button onClick={() => handleDeleteClick(editingProduct.ID_Variacao)} className="btn btn-danger">Excluir</button>
                                    </div>
                                </> 
                            ) : (
                                <> {/* Display mode */}
                                    {produto.URL_Imagem && (
                                        <img src={`http://localhost:3000/${produto.URL_Imagem}`} alt={produto.NomeProduto} className="product-image" />
                                    )}
                                    <h2 className="card-title">{produto.NomeProduto}</h2>
                                    <p className="card-text"><strong>Categoria:</strong> {produto.NomeCategoria || 'N/A'}</p>
                                    <p className="card-text">{produto.Descricao}</p>
                                    <p className="card-text"><strong>Variação:</strong> {produto.NomeVariacao} - {produto.ValorVariacao}</p>
                                    <p className="card-text"><strong>Preço:</strong> R$ {produto.Preco}</p>
                                    <p className="card-text"><strong>Estoque:</strong> {produto.QuantidadeDisponivel}</p>
                                    <button onClick={() => handleEditClick(produto)} className="btn btn-secondary">Editar</button>
                                </>
                            )}
                        </div>
                    ))}
                </div>
            )}
        </>
    );
};

export default MeusProdutos;
