import React from 'react';
import FormularioCadastro from '../components/FormularioCadastro';
import { Link } from 'react-router-dom'; // Importe Link se for adicionar um link para login

// Se o seu Global.css não é importado globalmente (ex: em App.js ou index.js),
// você precisará importá-lo aqui também:
// import '../Global.css'; // Ajuste o caminho conforme a localização do seu Global.css

const Cadastro = () => {
    return (
        // Usamos a tag <main> e a classe "apresentacao" para replicar o layout principal
        // que você já configurou no Global.css.
        <main className="apresentacao">
            {/* Esta seção conterá o formulário de cadastro */}
            {/* Usamos a classe "apresentacao__conteudo" para alinhar o formulário */}
            <section className="apresentacao__conteudo">
                {/* O título da página de cadastro */}
                <h1 className="apresentacao__conteudo__titulo">
                    Faça seu <strong className="titulodestaque">Cadastro!</strong>
                </h1>
                {/* Aqui seu componente de formulário de cadastro será renderizado */}
                <FormularioCadastro />

                {/* Opcional: Adicionar um link para a página de Login, similar ao da página de Login */}
                <p style={{ marginTop: '20px', textAlign: 'center', width: '100%', fontSize: '1.2rem', color: 'var(--cor-texto)' }}>
                    Já tem cadastro? <Link to="/login" style={{ color: 'var(--cor-primaria)', textDecoration: 'none', fontWeight: '600' }}>Faça Login aqui</Link>
                </p>
            </section>

            {/* A imagem do unicórnio da Lourdes, replicando o estilo da página inicial */}
            <img 
                src="/assets/ia.png" // Certifique-se de que o caminho para a imagem está correto
                alt="Unicórnio Lourdes" 
                className="apresentacao_imagem" 
            />
        </main>
    );
};

export default Cadastro;
