import React, { useState, useEffect, useContext, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const AdicionarOferta = () => {
    const navigate = useNavigate();
    const { token } = useContext(AuthContext);

    // Estados do fluxo
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [loading, setLoading] = useState(false);
    const [notification, setNotification] = useState({ message: '', type: '' });

    // ESTADOS PARA MÚLTIPLAS OFERTAS E NOVAS VARIAÇÕES
    const [offers, setOffers] = useState({});
    const [showNewVariationForm, setShowNewVariationForm] = useState(false);
    const [newVariationName, setNewVariationName] = useState('');
    const [newVariationValue, setNewVariationValue] = useState('');

    // Efeito de busca de produtos
    useEffect(() => {
        if (searchTerm.length < 3) {
            setSearchResults([]);
            return;
        }
        const delayDebounceFn = setTimeout(() => {
            setLoading(true);
            axios.get(`/api/produtos/?search=${searchTerm}`, { headers: { Authorization: `Bearer ${token}` } })
                .then(res => setSearchResults(res.data))
                .catch(err => console.error("Erro na busca:", err))
                .finally(() => setLoading(false));
        }, 500);
        return () => clearTimeout(delayDebounceFn);
    }, [searchTerm, token]);
    
    // Função para recarregar o produto selecionado (útil após criar nova variação)
    const reloadSelectedProduct = async (productId) => {
        try {
            const response = await axios.get(`/api/produtos/${productId}/`, { headers: { Authorization: `Bearer ${token}` } });
            setSelectedProduct(response.data);
            
            // Reinicia a lista de ofertas com as novas variações
            const initialOffers = response.data.variacoes.reduce((acc, v) => {
                acc[v.id] = { preco: '', quantidade: '' };
                return acc;
            }, {});
            setOffers(initialOffers);

        } catch (error) {
            console.error("Erro ao recarregar o produto", error);
            showNotification("Não foi possível atualizar a lista de variações.", "error");
        }
    };


    const showNotification = (message, type) => {
        setNotification({ message, type });
        setTimeout(() => setNotification({ message: '', type: '' }), 3000);
    };

    const handleSelectProduct = (product) => {
        setSelectedProduct(product);
        setSearchTerm('');
        setSearchResults([]);
    };
    
    const handleResetSelection = () => {
        setSelectedProduct(null);
        setOffers({});
    };

    const handleOfferChange = (variationId, field, value) => {
        setOffers(prev => ({
            ...prev,
            [variationId]: { ...prev[variationId], [field]: value }
        }));
    };
    
    // --- LÓGICA PARA ADICIONAR UMA NOVA VARIAÇÃO ---
    const handleAddNewVariation = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/variacoes/', {
                produto: selectedProduct.id,
                nome: newVariationName,
                valor: newVariationValue
            }, { headers: { Authorization: `Bearer ${token}` } });
            
            showNotification('Nova variação adicionada com sucesso!', 'success');
            
            // Limpa o formulário e o esconde
            setShowNewVariationForm(false);
            setNewVariationName('');
            setNewVariationValue('');

            // Recarrega os dados do produto para mostrar a nova variação na lista
            reloadSelectedProduct(selectedProduct.id);

        } catch (err) {
            showNotification('Erro ao adicionar variação.', 'error');
            console.error(err);
        }
    };
    
    const handleSaveAllOffers = async () => {
        const offersToSave = Object.entries(offers)
            .filter(([id, data]) => data.preco && data.quantidade)
            .map(([id, data]) => ({
                variacao: id,
                preco: data.preco,
                quantidade_disponivel: data.quantidade,
            }));

        if (offersToSave.length === 0) {
            showNotification('Preencha o preço e o estoque de pelo menos uma variação.', 'error');
            return;
        }

        try {
            const savePromises = offersToSave.map(offer => 
                axios.post('/api/ofertas/', offer, { headers: { Authorization: `Bearer ${token}` } })
            );
            
            await Promise.all(savePromises);
            
            showNotification(`${offersToSave.length} oferta(s) cadastrada(s) com sucesso!`, 'success');
            handleResetSelection();

        } catch (err) {
            showNotification('Ocorreu um erro ao salvar as ofertas.', 'error');
            console.error(err);
        }
    };

    return (
        <div className="layout-logado-content">
            {notification.message && <div className={`notification ${notification.type}`}>{notification.message}</div>}
            
            <h1 className="dashboard-title">Adicionar Nova Oferta</h1>

            {!selectedProduct ? (
                // --- PASSO 1: BUSCA DO PRODUTO (código existente) ---
                <div className="form-container">
                    {/* ... seu código de busca ... */}
                </div>
            ) : (
                <div className="form-container" style={{maxWidth: '900px'}}>
                    <div className="selected-product-info">
                        <h3>{selectedProduct.nome}</h3>
                        <button onClick={handleResetSelection} className="btn-link">Buscar outro produto</button>
                    </div>

                    <h4>Preencha o preço e estoque para as variações que deseja vender:</h4>
                    
                    <div className="offers-list">
                        {selectedProduct.variacoes.map((variation) => (
                            <div key={variation.id} className="offer-item">
                                <div className="offer-variation-name">
                                    <strong>{variation.nome}:</strong> {variation.valor}
                                </div>
                                <div className="form-group">
                                    <input type="number" value={offers[variation.id]?.preco || ''} onChange={e => handleOfferChange(variation.id, 'preco', e.target.value)} placeholder="Preço (R$)"/>
                                </div>
                                <div className="form-group">
                                    <input type="number" value={offers[variation.id]?.quantidade || ''} onChange={e => handleOfferChange(variation.id, 'quantidade', e.target.value)} placeholder="Estoque"/>
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* --- BOTÃO E FORMULÁRIO PARA ADICIONAR NOVA VARIAÇÃO --- */}
                    {showNewVariationForm ? (
                        <form onSubmit={handleAddNewVariation} className="new-variation-form">
                            <h5>Cadastrar Nova Variação</h5>
                            <div className="form-group">
                                <label>Nome da Variação (ex: Cor, Tamanho)</label>
                                <input type="text" value={newVariationName} onChange={e => setNewVariationName(e.target.value)} required/>
                            </div>
                             <div className="form-group">
                                <label>Valor da Variação (ex: Verde, G)</label>
                                <input type="text" value={newVariationValue} onChange={e => setNewVariationValue(e.target.value)} required/>
                            </div>
                            <div className="form-actions">
                                <button type="submit" className="btn btn-success">Salvar Variação</button>
                                <button type="button" onClick={() => setShowNewVariationForm(false)} className="btn btn-secondary">Cancelar</button>
                            </div>
                        </form>
                    ) : (
                        <button onClick={() => setShowNewVariationForm(true)} className="btn btn-secondary">+ Adicionar nova variação</button>
                    )}

                    <hr style={{margin: '2rem 0'}} />

                    <div className="form-actions">
                        <button onClick={handleSaveAllOffers} className="btn btn-primary">Salvar Todas as Ofertas Preenchidas</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdicionarOferta;
