import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const DashboardVendedor = () => {
  const navigate = useNavigate();
  const { token } = useContext(AuthContext);
  const [avaliacoes, setAvaliacoes] = useState([]);
  const [loadingAvaliacoes, setLoadingAvaliacoes] = useState(true);
  const [errorAvaliacoes, setErrorAvaliacoes] = useState('');
  const [showSuggestionForm, setShowSuggestionForm] = useState(false);
  const [suggestionText, setSuggestionText] = useState('');
  const [suggestionStatus, setSuggestionStatus] = useState('');

  const handleSendSuggestion = async () => {
    setSuggestionStatus('');
    if (!suggestionText.trim()) {
      setSuggestionStatus('A sugestão não pode ser vazia.');
      return;
    }
    try {
      await axios.post('/usuarios/suggestions', { suggestion: suggestionText }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuggestionStatus('Sugestão enviada com sucesso!');
      setSuggestionText('');
      setShowSuggestionForm(false);
    } catch (err) {
      setSuggestionStatus('Falha ao enviar sugestão.');
      console.error(err);
    }
  };

  const avaliacaoMedia = avaliacoes.length > 0
    ? (avaliacoes.reduce((acc, curr) => acc + curr.Nota, 0) / avaliacoes.length).toFixed(2)
    : 'N/A';



  useEffect(() => {
    const fetchAvaliacoes = async () => {
      try {
        const response = await axios.get('/usuarios/avaliacoes', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setAvaliacoes(response.data);
      } catch (err) {
        setErrorAvaliacoes('Falha ao buscar suas avaliações.');
        console.error(err);
      }
      setLoadingAvaliacoes(false);
    };

    if (token) {
      fetchAvaliacoes();
    }
  }, [token]);

  // Dados de exemplo para a lista de produtos mais acessados
  const produtosMaisAcessados = [
    { id: 1, nome: 'Produto A', acessos: 150 },
    { id: 2, nome: 'Produto B', acessos: 120 },
    { id: 3, nome: 'Produto C', acessos: 95 },
  ];

  return (
    <>
      <div className="dashboard-background-layer"></div>
      <div className="dashboard-container">
        <h1 className="dashboard-title">Painel do Vendedor</h1>
        <p className="text" style={{ textAlign: 'center', marginBottom: '30px' }}>Bem-vindo ao seu painel! Gerencie seus produtos e acompanhe seu desempenho.</p>

        {/* Seção de Gerenciamento de Produtos */}
        <div className="card-grid">
          <div className="card">
            <span className="card-icon">📦</span> {/* Ícone de pacote */}
            <h2 className="card-title">Gerenciamento de Produtos</h2>
            <p className="card-text">Adicione novos produtos ou visualize e edite os existentes.</p>
            <button onClick={() => navigate('/cadastrar-produto')} className="btn btn-primary">Cadastrar Novo Produto</button>
            <button onClick={() => navigate('/meus-produtos')} className="btn btn-secondary" style={{ marginTop: '10px' }}>Ver Meus Produtos</button>
          </div>

          {/* Seção de Análise */}
          <div className="card">
            <span className="card-icon">📈</span> {/* Ícone de gráfico */}
            <h2 className="card-title">Produtos Mais Acessados</h2>
            <p className="card-text">Acompanhe quais produtos estão gerando mais interesse.</p>
            <ul>
              {produtosMaisAcessados.map(produto => (
                <li key={produto.id} className="text" style={{ marginBottom: '5px' }}>
                  {produto.nome} - <strong>{produto.acessos} visualizações</strong>
                </li>
              ))}
            </ul>
          </div>

          {/* Seção de Conta */}
          <div className="card">
            <span className="card-icon">⚙️</span> {/* Ícone de engrenagem */}
            <h2 className="card-title">Minha Conta</h2>
            <p className="card-text">Edite suas informações de perfil e dados da loja.</p>
            <button onClick={() => navigate('/editar-perfil-vendedor')} className="btn btn-secondary">Editar Perfil e Dados da Loja</button>
            <p className="text" style={{ fontSize: '0.8rem', marginTop: '10px' }}><small>Obs: A alteração de dados como CNPJ requer aprovação de um administrador.</small></p>
          </div>

          {/* Seção de Avaliações */}
          <div className="card">
            <span className="card-icon">⭐</span> {/* Ícone de estrela */}
            <h2 className="card-title">Minhas Avaliações</h2>
            {loadingAvaliacoes ? (
              <p>Carregando avaliações...</p>
            ) : errorAvaliacoes ? (
              <p>{errorAvaliacoes}</p>
            ) : (
              <>
                <p className="card-text">Avaliação Média: <strong>{avaliacaoMedia}</strong></p>
                {avaliacoes.length === 0 ? (
                  <p>Você ainda não possui avaliações.</p>
                ) : (
                  <p>Você possui {avaliacoes.length} avaliação(ões).</p>
                )}
                <button 
                  onClick={() => navigate('/minhas-avaliacoes')}
                  className="btn btn-secondary"
                  style={{ marginTop: '10px' }}
                  disabled={avaliacoes.length === 0}
                >
                  Ver Todas as Avaliações
                </button>
              </>
            )}
            <button 
              onClick={() => setShowSuggestionForm(!showSuggestionForm)}
              className="btn btn-secondary"
              style={{ marginTop: '10px' }}
            >
              {showSuggestionForm ? 'Cancelar Sugestão' : 'Enviar Sugestão'}
            </button>

            {showSuggestionForm && (
              <div style={{ marginTop: '20px', width: '100%' }}>
                <textarea
                  placeholder="Digite sua sugestão aqui..."
                  value={suggestionText}
                  onChange={(e) => setSuggestionText(e.target.value)}
                  rows="5"
                  className="form-control"
                  style={{ width: '100%', marginBottom: '10px' }}
                ></textarea>
                <button onClick={handleSendSuggestion} className="btn btn-primary">
                  Enviar Sugestão
                </button>
                {suggestionStatus && <p className="message-info">{suggestionStatus}</p>}
              </div>
            )}

            <button 
              onClick={() => navigate('/indicar-vendedor')}
              className="btn btn-primary"
              style={{ marginTop: '10px' }}
            >
              Indicar Novo Vendedor
            </button>
          </div>
        </div>

        <div style={{ textAlign: 'center', marginTop: '30px' }}>
          <button onClick={() => navigate('/')} className="btn btn-secondary">Voltar ao Início</button>
        </div>
      </div>
    </>
  );
};

export default DashboardVendedor;
