import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const AvaliacaoVendedor = () => {
    const { idVendedor } = useParams();
    const [avaliacoes, setAvaliacoes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const { token } = useContext(AuthContext);

    useEffect(() => {
        const fetchAvaliacoes = async () => {
            try {
                const response = await axios.get(`/api/avaliacoes/vendedor/${idVendedor}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setAvaliacoes(response.data);
            } catch (err) {
                setError('Falha ao buscar avaliações.');
                console.error(err);
            }
            setLoading(false);
        };

        if (token) {
            fetchAvaliacoes();
        }
    }, [token, idVendedor]);

    if (loading) return <div>Carregando...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="container">
            <h1>Avaliações do Vendedor</h1>
            {avaliacoes.length === 0 ? (
                <p>Este vendedor ainda não possui avaliações.</p>
            ) : (
                <div className="evaluation-list">
                    {avaliacoes.map(avaliacao => (
                        <div key={avaliacao.id} className="evaluation-card">
                            <p><strong>Nota:</strong> {avaliacao.nota}/5</p>
                            <p><strong>Comentário:</strong> {avaliacao.comentario}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default AvaliacaoVendedor;
