import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const FormularioCadastro = () => {
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [tipoUsuario, setTipoUsuario] = useState('Cliente');
    const [erro, setErro] = useState(null);

    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErro(null);

        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/auth/cadastro`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password: senha, userType: tipoUsuario }),
            });

            const data = await response.json();

            if (response.ok) {
                // Redireciona para a página de completar o perfil com os dados necessários
                navigate('/CompletarPerfil', { state: { userId: data.userId, userType: data.userType } });
            } else {
                setErro(data.message || 'Ocorreu um erro no cadastro.');
            }
        } catch (error) {
            console.error('Falha ao conectar com a API:', error);
            setErro('Não foi possível conectar ao servidor. Tente novamente mais tarde.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Cadastrar</h2>
            {erro && <p style={{ color: 'red' }}>{erro}</p>}
            <div>
                <label>Email:</label>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Senha:</label>
                <input
                    type="password"
                    value={senha}
                    onChange={(e) => setSenha(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Tipo de Usuário:</label>
                <select value={tipoUsuario} onChange={(e) => setTipoUsuario(e.target.value)}>
                    <option value="Cliente">Cliente</option>
                    <option value="Vendedor">Vendedor</option>
                </select>
            </div>
            <button type="submit">Cadastrar</button>
        </form>
    );
};

export default FormularioCadastro;
