import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

// Formulário para dados do Cliente
const FormularioCliente = ({ userId, onSubmit }) => {
    const [nome, setNome] = useState('');
    const [cpf, setCpf] = useState('');
    const [telefone, setTelefone] = useState('');
    const [endereco, setEndereco] = useState('');
    const [dataNascimento, setDataNascimento] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit({ userId, nome, cpf, telefone, endereco, dataNascimento });
    };

    return (
        <form onSubmit={handleSubmit}>
            <h3>Complete seu Perfil de Cliente</h3>
            <input type="text" value={nome} onChange={(e) => setNome(e.target.value)} placeholder="Nome Completo" required />
            <input type="text" value={cpf} onChange={(e) => setCpf(e.target.value)} placeholder="CPF" required />
            <input type="text" value={telefone} onChange={(e) => setTelefone(e.target.value)} placeholder="Telefone" />
            <input type="text" value={endereco} onChange={(e) => setEndereco(e.target.value)} placeholder="Endereço" />
            <input type="date" value={dataNascimento} onChange={(e) => setDataNascimento(e.target.value)} placeholder="Data de Nascimento" />
            <button type="submit">Salvar e Continuar</button>
        </form>
    );
};

// Formulário para dados do Vendedor
const FormularioVendedor = ({ userId, onSubmit }) => {
    const [nomeLoja, setNomeLoja] = useState('');
    const [cnpj, setCnpj] = useState('');
    const [endereco, setEndereco] = useState('');
    const [telefone, setTelefone] = useState('');
    const [fundacao, setFundacao] = useState('');
    const [horarioFuncionamento, setHorarioFuncionamento] = useState('');
    const [nomeResponsavel, setNomeResponsavel] = useState('');
    const [cpfResponsavel, setCpfResponsavel] = useState('');
    const [breveDescricaoLoja, setBreveDescricaoLoja] = useState('');
    const [logotipoLoja, setLogotipoLoja] = useState('');
    const [websiteRedesSociais, setWebsiteRedesSociais] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // A categoria da loja está sendo fixada como 1 por enquanto.
        onSubmit({
            userId, nomeLoja, cnpj, endereco, telefone, fundacao, horarioFuncionamento,
            nomeResponsavel, cpfResponsavel, breveDescricaoLoja, logotipoLoja, websiteRedesSociais,
            idCategoriaLoja: 1
        });
    };

    return (
        <form onSubmit={handleSubmit}>
            <h3>Complete seu Perfil de Vendedor</h3>
            <input type="text" value={nomeLoja} onChange={(e) => setNomeLoja(e.target.value)} placeholder="Nome da Loja" required />
            <input type="text" value={cnpj} onChange={(e) => setCnpj(e.target.value)} placeholder="CNPJ" />
            <input type="text" value={endereco} onChange={(e) => setEndereco(e.target.value)} placeholder="Endereço da Loja" />
            <input type="text" value={telefone} onChange={(e) => setTelefone(e.target.value)} placeholder="Telefone da Loja" />
            <input type="date" value={fundacao} onChange={(e) => setFundacao(e.target.value)} placeholder="Data de Fundação" />
            <input type="text" value={horarioFuncionamento} onChange={(e) => setHorarioFuncionamento(e.target.value)} placeholder="Horário de Funcionamento" />
            <input type="text" value={nomeResponsavel} onChange={(e) => setNomeResponsavel(e.target.value)} placeholder="Nome do Responsável" />
            <input type="text" value={cpfResponsavel} onChange={(e) => setCpfResponsavel(e.target.value)} placeholder="CPF do Responsável" />
            <textarea value={breveDescricaoLoja} onChange={(e) => setBreveDescricaoLoja(e.target.value)} placeholder="Breve Descrição da Loja"></textarea>
            <input type="text" value={logotipoLoja} onChange={(e) => setLogotipoLoja(e.target.value)} placeholder="URL do Logotipo da Loja" />
            <input type="text" value={websiteRedesSociais} onChange={(e) => setWebsiteRedesSociais(e.target.value)} placeholder="Website/Redes Sociais" />
            <button type="submit">Salvar e Continuar</button>
        </form>
    );
};

const CompletarPerfil = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const [erro, setErro] = useState(null);

    // Os dados vêm do redirecionamento do formulário de cadastro
    const { userId, userType, email } = location.state || {};

    if (!userId || !userType) {
        return <p>Erro: Dados de usuário não encontrados. Por favor, <a href="/cadastro">tente se cadastrar novamente</a>.</p>;
    }

    const handleProfileSubmit = async (profileData) => {
        console.log('Dados do perfil enviados:', profileData); // Adicionado para depuração
        const endpoint = userType === 'Cliente' 
            ? '/users/client/complete-profile' 
            : '/users/seller/complete-profile';

        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}${endpoint}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(profileData),
            });

            const data = await response.json();

            if (response.ok) {
                // Sucesso! Redireciona para a verificação de email.
                // Passa o email que o usuário usou no cadastro inicial.
                navigate('/verificar-email', { state: { email: email } });
            } else {
                setErro(data.message || 'Ocorreu um erro ao salvar o perfil.');
            }
        } catch (error) {
            setErro('Falha ao conectar com o servidor.');
        }
    };

    return (
        <div>
            <h2>Quase lá!</h2>
            <p>Por favor, complete as informações abaixo para finalizar seu cadastro.</p>
            {erro && <p style={{ color: 'red' }}>{erro}</p>}

            {userType === 'Cliente' ? (
                <FormularioCliente userId={userId} onSubmit={handleProfileSubmit} />
            ) : (
                <FormularioVendedor userId={userId} onSubmit={handleProfileSubmit} />
            )}
        </div>
    );
};

export default CompletarPerfil;
