import React, { useContext } from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const RotaProtegida = ({ children, papeisPermitidos }) => {
    const { usuario, token, perfilCompleto, emailVerificado, email } = useContext(AuthContext);
    const location = useLocation();

    // Se não há token, redireciona para o login
    if (!token) {
        return <Navigate to="/login" replace />;
    }

    // Se há token, mas o usuário ainda não foi carregado (assíncrono), mostra carregando
    if (token && !usuario) {
        return <div>Carregando informações do usuário...</div>;
    }

    // Se o e-mail não for verificado, redireciona para a página de verificação, a menos que seja a página de perfil completo
    // ou a própria página de verificação.
    if (!emailVerificado && location.pathname !== '/verificar-email' && location.pathname !== '/completar-perfil') {
        return <Navigate to="/verificar-email" replace state={{ email: email }} />;
    }

    // Se o usuário está autenticado mas o perfil não está completo, redireciona
    if (usuario && !perfilCompleto && location.pathname !== '/completar-perfil') {
        return <Navigate to="/completar-perfil" replace state={{ from: location, idUsuario: usuario.id, tipoUsuario: usuario.tipoUsuario, email: email }} />;
    }

    // Se 'papeisPermitidos' for fornecido, verifica se o tipo de usuário está na lista
    if (papeisPermitidos && !papeisPermitidos.map(p => p.toLowerCase()).includes(usuario?.tipoUsuario?.toLowerCase())) {
        return <Navigate to="/nao-autorizado" replace />;
    }

    // Se tudo estiver ok, renderiza o conteúdo filho
    return children;
};

export default RotaProtegida;
