import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

const MeusProdutos = () => {
    const [produtos, setProdutos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const { token } = useContext(AuthContext);
    const [categorias, setCategorias] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');

    useEffect(() => {
        const fetchProdutos = async () => {
            try {
                const url = selectedCategory 
                    ? `/produtos/meus-produtos?idCategoria=${selectedCategory}` 
                    : '/produtos/meus-produtos';
                const response = await axios.get(url, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setProdutos(response.data);
            } catch (err) {
                setError('Falha ao buscar produtos.');
                console.error(err);
            }
            setLoading(false);
        };

        const fetchCategorias = async () => {
            try {
                const response = await axios.get('/categories');
                setCategorias(response.data);
            } catch (err) {
                console.error('Erro ao buscar categorias:', err);
            }
        };

        if (token) {
            fetchProdutos();
            fetchCategorias();
        }
    }, [token, selectedCategory]);

    if (loading) return <div>Carregando...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="container">
            <h1>Meus Produtos</h1>
            {produtos.length === 0 ? (
                <p>Você ainda não cadastrou nenhum produto.</p>
            ) : (
                <div className="product-list">
                    {produtos.map(produto => (
                        <div key={produto.ID_Variacao} className="product-card">
                            <h2>{produto.NomeProduto}</h2>
                            <p>{produto.Descricao}</p>
                            <p><strong>Variação:</strong> {produto.NomeVariacao} - {produto.ValorVariacao}</p>
                            <p><strong>Preço:</strong> R$ {produto.Preco}</p>
                            <p><strong>Estoque:</strong> {produto.QuantidadeDisponivel}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default MeusProdutos;
